oxy_webhelp_navlinks = {"K8s_crd_rsc-d22872e30436":{"topics":[{"title":"Kubernetes ClusterRoles","shortdesc":"<p class=\"shortdesc\"><span class=\"ph\">RSC<\/span> includes the kupr-admin and kupr-user ClusterRoles.<\/p>","href":"kubernetes\/k8s_clusterroles.html","attributes":{"data-id":"k8s_clusterroles"},"menu":{"hasChildren":false},"tocID":"k8s_clusterroles-d22872e30452","topics":[]},{"title":"Kubernetes Protection CRD requirements","shortdesc":"<p class=\"shortdesc\">To use Kubernetes Protection custom resource definitions you must have certain         resources available.<\/p>","href":"kubernetes\/k8s_crd_prerqs.html","attributes":{"data-id":"k8s_crd_prerqs"},"menu":{"hasChildren":false},"tocID":"k8s_crd_prerqs-d22872e30465","topics":[]},{"title":"Kubernetes protection CRDs","shortdesc":"<p class=\"shortdesc\">You can perform backup and recovery workflows using native APIs for Kubernetes by         using the Rubrik custom resource definitions.<\/p>","href":"kubernetes\/k8s_custom_resources_k8s_protection.html","attributes":{"data-id":"k8s_custom_resources_k8s_protection"},"menu":{"hasChildren":false},"tocID":"k8s_custom_resources_k8s_protection-d22872e30476","topics":[]},{"title":"Verifying the Kubernetes cluster resources","shortdesc":"<p class=\"shortdesc\">Verify that the resources available on a Kubernetes cluster are adequate for the <span class=\"ph\">RSC<\/span> custom resources.<\/p>","href":"kubernetes\/k8s_validate_k8s_cluster_resources.html","attributes":{"data-id":"k8s_validate_k8s_cluster_resources"},"menu":{"hasChildren":false},"tocID":"k8s_validate_k8s_cluster_resources-d22872e30487","topics":[]},{"title":"Creating role bindings for working with CRDs","shortdesc":"<p class=\"shortdesc\">Create kupr-user and kupr-namespace-user role bindings for users that work with         Kubernetes custom resource definitions.<\/p>","href":"kubernetes\/k8s_create_bindings_k8s_crd.html","attributes":{"data-id":"k8s_create_bindings_k8s_crd"},"menu":{"hasChildren":false},"tocID":"k8s_create_bindings_k8s_crd-d22872e30501","topics":[]},{"title":"Hook CRD","shortdesc":"<p class=\"shortdesc\">The Hook custom resource definition can be used to run commands before and after         running a backup of a protection set.<\/p>","href":"kubernetes\/k8s_hook_crd.html","attributes":{"data-id":"k8s_hook_crd"},"menu":{"hasChildren":false},"tocID":"k8s_hook_crd-d22872e30512","next":"k8s_hook_crd-d22872e30512"},{"title":"K8SCluster CRD","shortdesc":"<p class=\"shortdesc\">The K8SCluster custom resource definition creates a read-only custom resource that         provides information about your Kubernetes clusters.<\/p>","href":"kubernetes\/k8s_cluster.html","attributes":{"data-id":"k8s_cluster"},"menu":{"hasChildren":false},"tocID":"k8s_cluster-d22872e30545","next":"k8s_cluster-d22872e30545"},{"title":"ProtectionSet CRD","shortdesc":"<p class=\"shortdesc\">The ProtectionSet custom resource definition provides information about an         application in the Kubernetes cluster. Rubrik Kubernetes data protection can use a         ProtectionSet custom resource to protect that application.<\/p>","href":"kubernetes\/k8s_protectionset.html","attributes":{"data-id":"k8s_protectionset"},"menu":{"hasChildren":false},"tocID":"k8s_protectionset-d22872e30578","next":"k8s_protectionset-d22872e30578"},{"title":"RecoveryJob CRD","shortdesc":"<p class=\"shortdesc\">The RecoveryJob CRD defines a namespace-level recovery job in the Rubrik cluster,         allowing application owners to use kubectl for snapshot recovery, with jobs retained as long         as tracked in the cluster metadata.<\/p>","href":"kubernetes\/k8s_recoveryjob.html","attributes":{"data-id":"k8s_recoveryjob"},"menu":{"hasChildren":false},"tocID":"k8s_recoveryjob-d22872e30611","next":"k8s_recoveryjob-d22872e30611"},{"title":"RubrikCluster CRD","shortdesc":"<p class=\"shortdesc\">The RubrikCluster custom resource definition can be used to view information about         the Rubrik clusters associated with a Kubernetes cluster.<\/p>","href":"kubernetes\/k8s_rubrikcluster.html","attributes":{"data-id":"k8s_rubrikcluster"},"menu":{"hasChildren":false},"tocID":"k8s_rubrikcluster-d22872e30644","next":"k8s_rubrikcluster-d22872e30644"},{"title":"SLADomain CRD","shortdesc":"<p class=\"shortdesc\">The SLADomain custom resource definition provides information about the SLA Domains         in a Rubrik cluster.<\/p>","href":"kubernetes\/k8s_sladomain.html","attributes":{"data-id":"k8s_sladomain"},"menu":{"hasChildren":false},"tocID":"k8s_sladomain-d22872e30678","next":"k8s_sladomain-d22872e30678"},{"title":"Snapshot CRD","shortdesc":"<p class=\"shortdesc\">The Snapshot custom resource definition provides details about protection set         snapshots.<\/p>","href":"kubernetes\/k8s_snapshot.html","attributes":{"data-id":"k8s_snapshot"},"menu":{"hasChildren":false},"tocID":"k8s_snapshot-d22872e30711","next":"k8s_snapshot-d22872e30711"},{"title":"SnapshotJob CRD","shortdesc":"<p class=\"shortdesc\">The SnapshotJob custom resource definition allows you to create and manage on-demand         and policy-driven snapshot jobs.<\/p>","href":"kubernetes\/k8s_snapshotjob.html","attributes":{"data-id":"k8s_snapshotjob"},"menu":{"hasChildren":false},"tocID":"k8s_snapshotjob-d22872e30744","next":"k8s_snapshotjob-d22872e30744"}]}};