oxy_webhelp_navlinks = {"about_db_clones-d22872e27270":{"topics":[{"title":"Database clone prerequisites","shortdesc":"<p class=\"shortdesc\">Before cloning, an Oracle database must meet all preliminary         requirements.<\/p>","href":"oracle\/db_clone_prereqs.html","attributes":{"data-id":"db_clone_prereqs"},"menu":{"hasChildren":false},"tocID":"db_clone_prereqs-d22872e27281","topics":[]},{"title":"Clone an Oracle database using a different database name","shortdesc":"<p class=\"shortdesc\">You can specify a different Oracle database name during a clone operation.<\/p>","href":"oracle\/clone_using_a_different_database_name.html","attributes":{"data-id":"clone_using_a_different_database_name"},"menu":{"hasChildren":false},"tocID":"clone_using_a_different_database_name-d22872e27292","topics":[]},{"title":"Cloning an Oracle database snapshot","shortdesc":"<p class=\"shortdesc\">Use the cloning process to create an Oracle database snapshot that you can use for         database recovery or for recovering data files from the clone.<\/p>","href":"oracle\/rbk_cloning_oracle_database_snapshot.html","attributes":{"data-id":"rbk_cloning_oracle_database_snapshot"},"menu":{"hasChildren":false},"tocID":"rbk_cloning_oracle_database_snapshot-d22872e27303","topics":[]},{"title":"Cloning an Oracle RAC database snapshot","shortdesc":"<p class=\"shortdesc\">Use the cloning process to create an Oracle RAC database snapshot. You can use the         database snapshot for database recovery or for recovering data files from the         clone.<\/p>","href":"oracle\/cloning_oracle_rac_database_snapshot.html","attributes":{"data-id":"rbk_cloning_oracle_rac_database_snapshot"},"menu":{"hasChildren":false},"tocID":"rbk_cloning_oracle_rac_database_snapshot-d22872e27314","topics":[]},{"title":"Optional Advanced Settings parameters for Oracle Clone","shortdesc":"<p class=\"shortdesc\">The Advanced Settings section of an Oracle Clone operation includes a number of         optional parameters.<\/p>","href":"oracle\/opt_adv_settings_params_using_clone.html","attributes":{"data-id":"opt_adv_settings_params_using_clone"},"menu":{"hasChildren":false},"tocID":"opt_adv_settings_params_using_clone-d22872e27325","topics":[]},{"title":"Failed clones management","shortdesc":"<p class=\"shortdesc\">Instances created by a failed Oracle Clone task automatically shut down. To ensure data integrity, you must remove any files,         directories, control files, and database entries placed during the failed task.<\/p>","href":"oracle\/failed_clones.html","attributes":{"data-id":"failed_clones"},"menu":{"hasChildren":false},"tocID":"failed_clones-d22872e27336","topics":[]}]}};