oxy_webhelp_navlinks = {"api_playground-d22908e39501":{"topics":[{"title":"Accessing API Playground","shortdesc":"<p class=\"shortdesc\">Access API Playground available in your <span class=\"ph\">RSC<\/span> domain by authenticating         to <span class=\"ph\">RSC<\/span> using your account credentials. With access to API Playground, you         can explore the Rubrik GraphQL schema and perform data security operations using GraphQL         queries and mutations.<\/p>","href":"gql\/accessing_api_playground.html","attributes":{"data-id":"accessing_api_playground"},"menu":{"hasChildren":false},"tocID":"accessing_api_playground-d22908e39518","topics":[]},{"title":"Non-persistence of service account sessions on API Playground","shortdesc":"<p class=\"shortdesc\">When you use a service account to run GraphQL API operations in API Playground, for         security reasons, API Playground does not persist the service account credentials and         renders them invalid when the service account is not in use.<\/p>","href":"gql\/sa_session_non_persistence_api_playground.html","attributes":{"data-id":"sa_session_non_persistence_api_playground"},"menu":{"hasChildren":false},"tocID":"sa_session_non_persistence_api_playground-d22908e39535","topics":[]},{"title":"Using a service account to run GraphQL APIs","shortdesc":"<p class=\"shortdesc\">In API Playground, to test and validate the use of a service account in automation         scripts, use a service account to run GraphQL queries and mutations.<\/p>","href":"gql\/using_sa_to_run_gql.html","attributes":{"data-id":"using_sa_to_run_gql"},"menu":{"hasChildren":false},"tocID":"using_sa_to_run_gql-d22908e39546","topics":[]},{"title":"API Playground user interface","shortdesc":"<p class=\"shortdesc\">The API Playground user interface in <span class=\"ph\">RSC<\/span> uses the GraphiQL integrated         development environment framework to run Rubrik GraphQL operations.<\/p>","href":"gql\/api_playground_ui.html","attributes":{"data-id":"api_playground_ui"},"menu":{"hasChildren":false},"tocID":"api_playground_ui-d22908e39557","topics":[]}]}};