oxy_webhelp_navlinks = {"arch_nw_throttling-d22908e6551":{"topics":[{"title":"Scheduled overrides for archival network throttling","shortdesc":"<p class=\"shortdesc\">If your network has archival network throttling configured, you can schedule periods         when the throttling bandwidth is overridden. By scheduling overrides, you can increase or         decrease the network bandwidth available to the Rubrik cluster for transferring archiving         data during specific periods, based on the bandwidth usage patterns of the         network.<\/p>","href":"archival\/arch_nw_throttling_overrides.html","attributes":{"data-id":"arch_nw_throttling_overrides"},"menu":{"hasChildren":false},"tocID":"arch_nw_throttling_overrides-d22908e6562","topics":[]},{"title":"Configuring archival network throttling and scheduling overrides","shortdesc":"<p class=\"shortdesc\">To free up bandwidth for backup and restore operations on your Rubrik cluster by         limiting the bandwidth used by the archiving jobs on the cluster, configure archival network         throttling for the Rubrik cluster. You can also configure the system to override archival         network throttling threshold during scheduled periods.<\/p>","href":"archival\/arch_configuring_nw_throttling.html","attributes":{"data-id":"arch_configuring_nw_throttling"},"menu":{"hasChildren":false},"tocID":"arch_configuring_nw_throttling-d22908e6573","topics":[]}]}};