oxy_webhelp_navlinks = {"archiving_dc_workloads_azure-d22908e5543":{"topics":[{"title":"Archive preparation in Azure","shortdesc":"<p class=\"shortdesc\">Preparing Azure storage for archiving involves creating a storage account in         Azure.<\/p>","href":"cdm\/archive_preparation_in_azure.html","attributes":{"data-id":"archive_preparation_in_azure"},"menu":{"hasChildren":false},"tocID":"archive_preparation_in_azure-d22908e5557","next":"archive_preparation_in_azure-d22908e5557"},{"title":"Creating an Azure cloud account with OAuth for data center archival locations","shortdesc":"<p class=\"shortdesc\">Create a cloud account in <span class=\"ph\">RSC<\/span> using OAuth configuration to provide             <span class=\"ph\">RSC<\/span> with service principal based access and permissions to manage a data         center archival location in an Azure storage account.<\/p>","href":"archival\/creating_azr_cloud_accnt_with_oauth_dc_arch_loc.html","attributes":{"data-id":"creating_azr_cloud_accnt_with_oauth_dc_arch_loc"},"menu":{"hasChildren":false},"tocID":"creating_azr_cloud_accnt_with_oauth_dc_arch_loc-d22908e5603","topics":[]},{"title":"Creating an Azure cloud account without OAuth for data center archival locations","shortdesc":"<p class=\"shortdesc\">Create a cloud account in <span class=\"ph\">RSC<\/span> without an OAuth configuration to         provide Rubrik with service principal based access and permissions to manage a data center         archival location in an Azure storage account.<\/p>","href":"archival\/creating_azr_cloud_accnt_without_oauth_dc_arch_loc.html","attributes":{"data-id":"creating_azr_cloud_accnt_without_oauth_dc_arch_loc"},"menu":{"hasChildren":false},"tocID":"creating_azr_cloud_accnt_without_oauth_dc_arch_loc-d22908e5620","next":"creating_azr_cloud_accnt_without_oauth_dc_arch_loc-d22908e5620"},{"title":"Creating an access key based Azure cloud account for data center archival locations","shortdesc":"<p class=\"shortdesc\">Create an Azure cloud account in <span class=\"ph\">RSC<\/span> to provide <span class=\"ph\">RSC<\/span>         with access key based access and permissions to manage a data center archival location in an         Azure storage account.<\/p>","href":"archival\/creating_azr_acc_ak_dc_arch_loc.html","attributes":{"data-id":"creating_azr_acc_ak_dc_arch_loc"},"menu":{"hasChildren":false},"tocID":"creating_azr_acc_ak_dc_arch_loc-d22908e5648","topics":[]},{"title":"Editing a service principal based Azure cloud account","shortdesc":"<p class=\"shortdesc\">Edit an Azure cloud account without OAuth, configured with the service principal         based authentication method to update the client secret key.<\/p>","href":"archival\/editing_service_principal_based_azr_cloud_account.html","attributes":{"data-id":"editing_service_principal_based_azr_cloud_account"},"menu":{"hasChildren":false},"tocID":"editing_service_principal_based_azr_cloud_account-d22908e5665","topics":[]},{"title":"Deleting a service principal based Azure cloud account","shortdesc":"<p class=\"shortdesc\">Delete an Azure cloud account configured with the service principal based         authentication method if you do not want <span class=\"ph\">RSC<\/span> to access the Azure         subscription specified in the cloud account.<\/p>","href":"archival\/deleting_service_principal_based_azr_cloud_account.html","attributes":{"data-id":"deleting_service_principal_based_azr_cloud_account"},"menu":{"hasChildren":false},"tocID":"deleting_service_principal_based_azr_cloud_account-d22908e5676","topics":[]},{"title":"Azure archival locations with immutable storage","shortdesc":"<p class=\"shortdesc\">Azure archival locations with immutable storage protect data from being modified or         deleted from archives. Rubrik supports immutable storage on Azure archival locations at the         blob level and at the container level.<\/p>","href":"cdm\/azr_immutable_archival_locations.html","attributes":{"data-id":"azr_immutable_archival_locations"},"menu":{"hasChildren":false},"tocID":"azr_immutable_archival_locations-d22908e5690","next":"azr_immutable_archival_locations-d22908e5690"},{"title":"Azure Archive tiering options","shortdesc":"<p class=\"shortdesc\">You can configure archive tiering for Azure archival locations to place older data to         the Archive tier based on an SLA Domain policy.<\/p>","href":"cdm\/azure_archive_tiering.html","attributes":{"data-id":"azure_archive_tiering"},"menu":{"hasChildren":false},"tocID":"azure_archive_tiering-d22908e5759","next":"azure_archive_tiering-d22908e5759"},{"title":"Azure archival location settings","shortdesc":"<p class=\"shortdesc\">Information about the settings required for creating an Azure archival location for a         Rubrik cluster.<\/p>","href":"archival\/azure_archival_location_settings.html","attributes":{"data-id":"azure_archival_location_settings"},"menu":{"hasChildren":false},"tocID":"azure_archival_location_settings-d22908e5798","topics":[]},{"title":"Azure cloud compute settings","shortdesc":"<p class=\"shortdesc\">Information about the cloud compute settings required for Archival Consolidation         during Azure archival location configuration.<\/p>","href":"archival\/arch_azure_cloud_compute_settings.html","attributes":{"data-id":"arch_azure_cloud_compute_settings"},"menu":{"hasChildren":false},"tocID":"arch_azure_cloud_compute_settings-d22908e5809","topics":[]},{"title":"Switching access types in Azure archival location settings","shortdesc":"<p class=\"shortdesc\">Switch the access type to either service principal based or access key based, by         editing the Azure archival location settings. The specified access type authenticates the             <span class=\"ph\">Rubrik Security Cloud<\/span> account for accessing the Azure storage account when creating         an archival location.<\/p>","href":"archival\/switching_access_types_azr_arch_loc_settings.html","attributes":{"data-id":"switching_access_types_azr_arch_loc_settings"},"menu":{"hasChildren":false},"tocID":"switching_access_types_azr_arch_loc_settings-d22908e5821","topics":[]},{"title":"Azure Cold tier support for archival location","shortdesc":"<p class=\"shortdesc\">When you want to store data for a longer duration and access the data infrequently,         you can transfer the data to an archival location from the default Hot or Cool access tier         to the Cold access tier by using the Azure storage lifecycle management policy.<\/p>","href":"archival\/azure_cold_tier_support_archival_location.html","attributes":{"data-id":"azure_cold_tier_support_archival_location"},"menu":{"hasChildren":false},"tocID":"azure_cold_tier_support_archival_location-d22908e5835","next":"azure_cold_tier_support_archival_location-d22908e5835"}]}};