oxy_webhelp_navlinks = {"aws_dynamodb_recovery-d22908e13398":{"topics":[{"title":"Recovery options for Amazon DynamoDB","shortdesc":"<p class=\"shortdesc\"><span class=\"ph\">RSC<\/span> provides two recovery options for Amazon DynamoDB tables:         point-in-time recovery for precise data restoration and snapshot recovery for flexible data         management.<\/p>","href":"saas\/aws_dynamodb_recovery_options.html","attributes":{"data-id":"aws_dynamodb_recovery_options"},"menu":{"hasChildren":false},"tocID":"aws_dynamodb_recovery_options-d22908e13412","topics":[]},{"title":"Networking requirements for Amazon DynamoDB recovery","shortdesc":"<p class=\"shortdesc\">You can ensure successful Amazon DynamoDB recovery by considering key networking         requirements such as provisioned Write Capacity Units, VPC configuration, security group         settings, and NACLs.<\/p>","href":"saas\/aws_dynamodb_recv_network_reqs.html","attributes":{"data-id":"aws_dynamodb_recv_network_reqs"},"menu":{"hasChildren":false},"tocID":"aws_dynamodb_recv_network_reqs-d22908e13425","topics":[]},{"title":"Performing point-in-time recovery for Amazon DynamoDB","shortdesc":"<p class=\"shortdesc\">Perform a point-in-time recovery for an Amazon DynamoDB table to restore data to a         specific point in time using continuous backups, with the ability to create the new table in         a different AWS region.<\/p>","href":"saas\/aws_dynamodb_performing_pitr.html","attributes":{"data-id":"aws_dynamodb_performing_pitr"},"menu":{"hasChildren":false},"tocID":"aws_dynamodb_performing_pitr-d22908e13436","topics":[]},{"title":"Performing snapshot recovery for Amazon DynamoDB","shortdesc":"<p class=\"shortdesc\">Perform a snapshot recovery for an Amazon DynamoDB table to recover data using either         an on-demand snapshot or a scheduled snapshot taken according to the assigned SLA         Domain.<\/p>","href":"saas\/aws_dynamodb_performing_snapshot_recv.html","attributes":{"data-id":"aws_dynamodb_performing_snapshot_recv"},"menu":{"hasChildren":false},"tocID":"aws_dynamodb_performing_snapshot_recv-d22908e13447","topics":[]}]}};