oxy_webhelp_navlinks = {"azr_configuration_exocompute-d22872e16358":{"topics":[{"title":"Creating a VNet","shortdesc":"<p class=\"shortdesc\">Create a VNet on the Azure portal to configure localized or centralized Exocompute         with Rubrik-hosted ACR and Rubrik-managed AKS clusters.<\/p>","href":"saas\/azr_creating_vnet.html","attributes":{"data-id":"azr_creating_vnet"},"menu":{"hasChildren":false},"tocID":"azr_creating_vnet-d22872e16369","topics":[]},{"title":"Configuring Exocompute for Rubrik-managed clusters","shortdesc":"<p class=\"shortdesc\">After creating an Exocompute host account for a Rubrik-managed cluster, configure an         Exocompute instance in the host account by adding a region.<\/p>","href":"saas\/azr_configuring_exo_host_acct_rmc.html","attributes":{"data-id":"azr_configuring_exo_host_acct_rmc"},"menu":{"hasChildren":false},"tocID":"azr_configuring_exo_host_acct_rmc-d22872e16380","topics":[]},{"title":"Mapping Azure accounts to centralized Exocompute","shortdesc":"<p class=\"shortdesc\">After you have configured an Exocompute instance in the Exocompute host subscription         by adding regions and confirmed that the Azure Kubernetes Service cluster is healthy, map an         application subscription to an Exocompute host subscription from the Azure page. Update or         remove an existing mapping as required.<\/p>","href":"saas\/azr_managing_exocompute_mapping.html","attributes":{"data-id":"azr_managing_exocompute_mapping"},"menu":{"hasChildren":false},"tocID":"azr_managing_exocompute_mapping-d22872e16391","topics":[]}]}};