oxy_webhelp_navlinks = {"azr_evaluate_options_workload_protect-d22908e16795":{"topics":[{"title":"Primary snapshot retention for Azure workloads","shortdesc":"<p class=\"shortdesc\">Primary snapshot retention for Azure workloads ensures data protection and         recoverability by allowing you to set various retention policies, such as retaining hourly,         daily, monthly, and yearly snapshots, to meet compliance, business continuity, and disaster         recovery requirements.<\/p>","href":"saas\/azr_primary_snapshot_retention.html","attributes":{"data-id":"azr_primary_snapshot_retention"},"menu":{"hasChildren":false},"tocID":"azr_primary_snapshot_retention-d22908e16806","topics":[]},{"title":"Archiving of Azure workloads","shortdesc":"<p class=\"shortdesc\">Archiving in <span class=\"ph\">RSC<\/span> tiers Azure virtual machine or managed disk         snapshots to lower-cost storage, ensuring cost savings, compliance, and         security.<\/p>","href":"saas\/azr_archiving_workloads.html","attributes":{"data-id":"azr_archiving_workloads"},"menu":{"hasChildren":false},"tocID":"azr_archiving_workloads-d22908e16817","topics":[]},{"title":"Multiple archival locations for Azure","shortdesc":"<p class=\"shortdesc\"><span class=\"ph\">RSC<\/span> supports multiple archival locations for Azure virtual machines         and managed disks, allowing granular control over data archiving with up to two distinct         archiving policies.<\/p>","href":"saas\/azr_multiple_arch_loc.html","attributes":{"data-id":"azr_multiple_arch_loc"},"menu":{"hasChildren":false},"tocID":"azr_multiple_arch_loc-d22908e16831","topics":[]},{"title":"Replication of Azure snapshots","shortdesc":"<p class=\"shortdesc\">Replication allows snapshots of Azure virtual machines and managed disks to be copied         to another region based on SLA Domain, using APIs to minimize costs. This process ensures         multi-region availability and data protection for virtual machines and managed         disks.<\/p>","href":"saas\/azr_replication_snapshots.html","attributes":{"data-id":"azr_replication_snapshots"},"menu":{"hasChildren":false},"tocID":"azr_replication_snapshots-d22908e16844","topics":[]},{"title":"Retention lock for Azure workloads","shortdesc":"<p class=\"shortdesc\">A retention lock on an <span class=\"ph\">RSC<\/span> SLA Domain is a security feature that         prevents premature deletion of Azure snapshots.<\/p>","href":"saas\/azr_retention_lock.html","attributes":{"data-id":"azr_retention_lock"},"menu":{"hasChildren":false},"tocID":"azr_retention_lock-d22908e16855","topics":[]}]}};