oxy_webhelp_navlinks = {"azr_manage_vm_md-d22872e17025":{"topics":[{"title":"Azure virtual machine snapshots","shortdesc":"<p class=\"shortdesc\">To manage and protect your data in <span class=\"ph\">RSC<\/span>, you can take an on-demand         snapshot or configure an SLA Domain to take scheduled snapshots based on your         requirements.<\/p>","href":"saas\/azr_snapshots.html","attributes":{"data-id":"azr_snapshots"},"menu":{"hasChildren":false},"tocID":"azr_snapshots-d22872e17039","next":"azr_snapshots-d22872e17039"},{"title":"Azure subscription and resource group management","shortdesc":"<p class=\"shortdesc\">Using <span class=\"ph\">RSC<\/span>, you can monitor and manage the details of your Azure         subscriptions and resource groups. You can also refresh an Azure subscription on-demand to         obtain the most up-to-date metadata based on your specific requirements.<\/p>","href":"saas\/subscription_management.html","attributes":{"data-id":"subscription_management"},"menu":{"hasChildren":false},"tocID":"subscription_management-d22872e17163","next":"subscription_management-d22872e17163"},{"title":"Azure virtual machines and managed disks details","shortdesc":"<p class=\"shortdesc\">Viewing and analyzing detailed information about Azure virtual machines and managed         disks helps in making informed decisions. Key aspects include protection status, detailed         attributes, snapshots calendar, and events log for audit and compliance.<\/p>","href":"saas\/azr_vm_md_details.html","attributes":{"data-id":"azr_vm_md_details"},"menu":{"hasChildren":false},"tocID":"azr_vm_md_details-d22872e17226","next":"azr_vm_md_details-d22872e17226"},{"title":"Azure custom tags","shortdesc":"<p class=\"shortdesc\">You can specify Azure custom tags in <span class=\"ph\">RSC<\/span> to implement specific         policies for cost allocation, financial management, and resource creation.<\/p>","href":"saas\/azr_custom_tag.html","attributes":{"data-id":"azr_custom_tag"},"menu":{"hasChildren":false},"tocID":"azr_custom_tag-d22872e17283","next":"azr_custom_tag-d22872e17283"},{"title":"Application-consistent protection for Azure virtual machines","shortdesc":"<p class=\"shortdesc\">Application-consistent backups preserve the working state of applications running on         a protected Azure virtual machine.<\/p>","href":"saas\/app_consistent_backups_in_azure.html","attributes":{"data-id":"app_consistent_backups_in_azure"},"menu":{"hasChildren":false},"tocID":"app_consistent_backups_in_azure-d22872e17313","next":"app_consistent_backups_in_azure-d22872e17313"},{"title":"Data Observability for Azure virtual machines and managed disks","shortdesc":"<p class=\"shortdesc\">To provide Data Observability, you can enable <span class=\"ph\">Anomaly         Detection<\/span> to detect ransomware attacks<span class=\"ph\">, and enable <span class=\"ph\">Data Security Posture<\/span>             to identify sensitive data<\/span> in backups for Azure virtual machines and managed         disks.<\/p>","href":"saas\/data_observability_for_azure_vms.html","attributes":{"data-id":"data_observability_for_azure_vms"},"menu":{"hasChildren":false},"tocID":"data_observability_for_azure_vms-d22872e17383","next":"data_observability_for_azure_vms-d22872e17383"}]}};