oxy_webhelp_navlinks = {"azr_snapshots-d22908e17037":{"topics":[{"title":"Private access to an Azure snapshot","shortdesc":"<p class=\"shortdesc\">When the public access network policy for an Azure disk is not enabled and you want             <span class=\"ph\">RSC<\/span> to access a snapshot using the private access network policy, you         must include specific permission categories for the associated Azure subscription and         Exocompute so that <span class=\"ph\">RSC<\/span> can access the snapshot.<\/p>","href":"saas\/private_access_azr_snapshot.html","attributes":{"data-id":"private_access_azr_snapshot"},"menu":{"hasChildren":false},"tocID":"private_access_azr_snapshot-d22908e17051","next":"private_access_azr_snapshot-d22908e17051"},{"title":"Management of Azure virtual machine snapshots","shortdesc":"<p class=\"shortdesc\">You can perform management tasks for the snapshots of Azure virtual machines and         managed disks. This includes taking on-demand snapshots, changing the retention policies,         and deleting the snapshots.<\/p>","href":"saas\/azr_vm_snapshot_management.html","attributes":{"data-id":"azr_vm_snapshot_management"},"menu":{"hasChildren":false},"tocID":"azr_vm_snapshot_management-d22908e17084","next":"azr_vm_snapshot_management-d22908e17084"}]}};