oxy_webhelp_navlinks = {"azr_storage_tiering_setup-d22908e16607":{"topics":[{"title":"Evaluate Azure storage tiering options","shortdesc":"<p class=\"shortdesc\">Before setting up Azure storage tiering, it is important to evaluate the tiering         options to align with archiving needs, balancing cost, performance, and data retrieval. Key         considerations include archival location, access tiers, instant tiering, endpoint usage, and         encryption standards.<\/p>","href":"saas\/azr_storage_tiering_evaluate_options.html","attributes":{"data-id":"azr_storage_tiering_evaluate_options"},"menu":{"hasChildren":false},"tocID":"azr_storage_tiering_evaluate_options-d22908e16618","next":"azr_storage_tiering_evaluate_options-d22908e16618"},{"title":"User permissions for configuring Azure storage tiering","shortdesc":"<p class=\"shortdesc\">To set up Azure storage tiering, the <span class=\"ph\">RSC<\/span> user must be assigned a role         with specific permissions.<\/p>","href":"saas\/azr_user_perms_config_storage_tiering.html","attributes":{"data-id":"azr_user_perms_config_storage_tiering"},"menu":{"hasChildren":false},"tocID":"azr_user_perms_config_storage_tiering-d22908e16699","topics":[]},{"title":"Configure Azure storage tiering","shortdesc":"<p class=\"shortdesc\">To configure Azure storage tiering, you must create an <span class=\"ph\">archival             location or a Rubrik Cloud Vault location for archiving snapshots and storing             backups<\/span>.         Additionally, you can configure a private or service endpoint to tier snapshots to an Azure         storage account.<\/p>","href":"saas\/azr_config_storage_tiering.html","attributes":{"data-id":"azr_config_storage_tiering"},"menu":{"hasChildren":false},"tocID":"azr_config_storage_tiering-d22908e16713","next":"azr_config_storage_tiering-d22908e16713"}]}};