oxy_webhelp_navlinks = {"azure_stack_hci_overview-d22872e20371":{"topics":[{"title":"Azure Local management","shortdesc":"<p class=\"shortdesc\">Azure Local (Azure Stack HCI) is a hyperconverged clustering solution that uses         validated hardware to run virtualized workloads on-premises. Adding Azure Local to <span class=\"ph\">RSC<\/span> recognizes it as a Hyper-V cluster and discovers these virtualized         workloads as Hyper-V virtual machines. You can utilize Rubrik Hyper-V management         capabilities to manage Azure Local workloads.<\/p>","href":"saas\/hyper_v\/azure_stack_hci_management.html","attributes":{"data-id":"azure_stack_hci_management"},"menu":{"hasChildren":false},"tocID":"azure_stack_hci_management-d22872e20385","topics":[]},{"title":"Azure Local limitations","shortdesc":"<p class=\"shortdesc\">Recovering virtual machines to an Azure Local (Azure Stack HCI) has certain         limitations, but these limitations have workarounds.<\/p>","href":"saas\/hyper_v\/azure_stack_hci_limitations.html","attributes":{"data-id":"azure_stack_hci_limitations"},"menu":{"hasChildren":false},"tocID":"azure_stack_hci_limitations-d22872e20399","topics":[]}]}};