oxy_webhelp_navlinks = {"config_prereqs-d23139e25483":{"topics":[{"title":"System requirements for Oracle databases","shortdesc":"<p class=\"shortdesc\">Information about the system requirements for Oracle databases on the Rubrik     cluster.<\/p>","href":"oracle\/sys_reqs_for_oracle_dbs.html","attributes":{"data-id":"sys_reqs_for_oracle_dbs"},"menu":{"hasChildren":false},"tocID":"sys_reqs_for_oracle_dbs-d23139e25494","next":"sys_reqs_for_oracle_dbs-d23139e25494"},{"title":"Oracle support requirements on Windows","shortdesc":"<p class=\"shortdesc\"><span class=\"ph\">RSC<\/span> includes support requirements to protect Oracle databases on         Windows.<\/p>","href":"oracle\/reqs_oracle_windows_support.html","attributes":{"data-id":"reqs_oracle_windows_support"},"menu":{"hasChildren":false},"tocID":"reqs_oracle_windows_support-d23139e25516","next":"reqs_oracle_windows_support-d23139e25516"},{"title":"Oracle 23ai known problems and workarounds","shortdesc":"<p class=\"shortdesc\"><span class=\"ph\">RSC<\/span> workload protection for Oracle 23ai databases running with         on-premises Oracle Exadata has known issues, but there are available workarounds. <\/p>","href":"oracle\/oracle_23ai_problems_workarounds.html","attributes":{"data-id":"oracle_23ai_problems_workarounds"},"menu":{"hasChildren":false},"tocID":"oracle_23ai_problems_workarounds-d23139e25540","topics":[]},{"title":"Creating an empty oratab file","shortdesc":"<p class=\"shortdesc\">If the Oracle host has no databases running, create an empty             <span class=\"ph filepath\">\/etc\/oratab<\/span> file before installing RBS software.<\/p>","href":"oracle\/creating_empty_etc_oratab_file.html","attributes":{"data-id":"creating_empty_etc_oratab_file"},"menu":{"hasChildren":false},"tocID":"creating_empty_etc_oratab_file-d23139e25553","topics":[]},{"title":"Bigfile tablespace restore for Oracle databases","shortdesc":"<p class=\"shortdesc\">The Rubrik cluster supports restoring Oracle databases that are configured for         bigfile tablespaces, which are used use to create very large data or temporary files. <\/p>","href":"oracle\/bigfile_tablespace_restore_for_orcl_dbs.html","attributes":{"data-id":"bigfile_tablespace_restore_for_orcl_dbs"},"menu":{"hasChildren":false},"tocID":"bigfile_tablespace_restore_for_orcl_dbs-d23139e25567","topics":[]},{"title":"Supported data transfer mechanisms for Oracle databases","shortdesc":"<p class=\"shortdesc\">For Oracle databases, the Rubrik cluster supports the NFS and Oracle Secure Thrift         data transfer mechanisms. By using these mechanisms, you can transfer data securely between         the Rubrik cluster and the Oracle host.<\/p>","href":"oracle\/supported_data_transfer_mechanisms_oracle.html","attributes":{"data-id":"supported_data_transfer_mechanisms_oracle"},"menu":{"hasChildren":false},"tocID":"supported_data_transfer_mechanisms_oracle-d23139e25578","next":"supported_data_transfer_mechanisms_oracle-d23139e25578"}]}};