oxy_webhelp_navlinks = {"data_classification_mgmt-d22872e37067":{"topics":[{"title":"Supported file types","shortdesc":"<p class=\"shortdesc\">Information about the file types that <span class=\"ph\">Data Security Posture<\/span> analyzes and         performs deep inspection for  classifying the data discovered.<\/p>","href":"dspm\/supported_file_types.html","attributes":{"data-id":"supported_file_types"},"menu":{"hasChildren":false},"tocID":"supported_file_types-d22872e37087","topics":[]},{"title":"Assigning resources for classification","shortdesc":"<p class=\"shortdesc\">You can assign <span class=\"ph\">data categories<\/span> to resources such as objects or         clusters located in data centers, M365 workloads, and optionally to Azure virtual machines.         After assignment, <span class=\"ph\">Data Security Posture<\/span> scans resources to detect related <span class=\"ph\">data types<\/span>. Cloud objects are scanned automatically.<\/p>","href":"dspm\/assigning_resources_classification.html","attributes":{"data-id":"assigning_resources_classification"},"menu":{"hasChildren":false},"tocID":"assigning_resources_classification-d22872e37101","topics":[]},{"title":"Predefined <span class=\"ph\">data types<\/span>","shortdesc":"<p class=\"shortdesc\">Predefined <span class=\"ph\">data types<\/span> are used in predefined <span class=\"ph\">data categories<\/span>. They are available in <span class=\"ph\">RSC<\/span> for creating         custom <span class=\"ph\">data categories<\/span> to detect sensitive data.<\/p>","href":"dspm\/predefined_data_categories.html","attributes":{"data-id":"predefined_data_categories"},"menu":{"hasChildren":false},"tocID":"predefined_data_categories-d22872e37121","topics":[]},{"title":"Creating a <span class=\"ph\">data category<\/span>","shortdesc":"<p class=\"shortdesc\">Create a <span class=\"ph\">data category<\/span> to look for specific classification information         in <span class=\"ph\">Data Security Posture<\/span>. <\/p>","href":"dspm\/creating_data_category.html","attributes":{"data-id":"creating_data_category"},"menu":{"hasChildren":false},"tocID":"creating_data_category-d22872e37146","topics":[]},{"title":"Creating a <span class=\"ph\">data type<\/span>","shortdesc":"<p class=\"shortdesc\">You can create customized <span class=\"ph\">data types<\/span> to reflect your business and         security policies and processes.<\/p>","href":"dspm\/creating_data_type.html","attributes":{"data-id":"creating_data_type"},"menu":{"hasChildren":false},"tocID":"creating_data_type-d22872e37165","topics":[]},{"title":"On-demand classification","shortdesc":"<p class=\"shortdesc\">On-demand classification jobs run against the most recent snapshot of the specified         resources to generate classification results and reports.<\/p>","href":"saas\/on-demand_classification.html","attributes":{"data-id":"On-demand_classification"},"menu":{"hasChildren":false},"tocID":"On-demand_classification-d22872e37181","next":"On-demand_classification-d22872e37181"},{"title":"Data masking","shortdesc":"<p class=\"shortdesc\">Data masking replaces sensitive values in sampled data with obfuscated substitutes.         This feature protects privacy while preserving the ability to understand data classified by             <span class=\"ph\">Data Discovery and Classification<\/span>.<\/p>","href":"dspm\/data_masking.html","attributes":{"data-id":"data_masking"},"menu":{"hasChildren":false},"tocID":"data_masking-d22872e37259","next":"data_masking-d22872e37259"}]}};