oxy_webhelp_navlinks = {"db2_cross_host_recovery-d22872e24628":{"topics":[{"title":"Enabling cross-host recovery for Db2 databases","shortdesc":"<p class=\"shortdesc\">Enable cross-host recovery for Db2 databases to authorize restoring Db2 files to a         host other than its original host. This feature is available for Rubrik CDM versions 9.3.1         and later.<\/p>","href":"db2\/enabling_cross_host_recovr_db2_databases.html","attributes":{"data-id":"enabling_cross_host_recovr_db2_databases"},"menu":{"hasChildren":false},"tocID":"enabling_cross_host_recovr_db2_databases-d22872e24639","topics":[]},{"title":"Editing cross-host recovery for Db2 databases","shortdesc":"<p class=\"shortdesc\">Edit cross-host recovery settings for Db2 databases to add or remove hosts where the         database can be restored.<\/p>","href":"db2\/editing_cross_host_recovr_db2_databases.html","attributes":{"data-id":"editing_cross_host_recovr_db2_databases"},"menu":{"hasChildren":false},"tocID":"editing_cross_host_recovr_db2_databases-d22872e24650","topics":[]},{"title":"Disabling cross-host recovery for Db2 databases","shortdesc":"<p class=\"shortdesc\">Disable cross-host recovery for a Db2 database to prevent its files from being         restored to a host other than its original host.<\/p>","href":"db2\/disabling_cross_host_recovr_db2_databases.html","attributes":{"data-id":"disabling_cross_host_recovr_db2_databases"},"menu":{"hasChildren":false},"tocID":"disabling_cross_host_recovr_db2_databases-d22872e24661","topics":[]}]}};