oxy_webhelp_navlinks = {"db2_data_retrieval-d22908e24669":{"topics":[{"title":"Retrieving the Db2 backup ID and backup type of a snapshot","shortdesc":"<p class=\"shortdesc\">To restore the Db2 backup and recovery process, retrieve the backup ID and backup         type associated with a snapshot. These attributes serve as distinctive identifiers and         define the category of a backup.<\/p>","href":"db2\/retrieving_db2_backup_id_type_snapshot.html","attributes":{"data-id":"retrieving_db2_backup_id_type_snapshot"},"menu":{"hasChildren":false},"tocID":"retrieving_db2_backup_id_type_snapshot-d22908e24680","topics":[]},{"title":"Downloading a Db2 data backup image on Linux","shortdesc":"<p class=\"shortdesc\">From a set of data backup images, download the data backup image for a database using         the standalone, Database Partitioning Feature, or High Availability Disaster Recovery         configurations. Then, use the downloaded backup image to verify the Db2 data retrieval         process on CDM versions 9.0.1 or later.<\/p>","href":"db2\/downloading_db2_data_backup_img_linux.html","attributes":{"data-id":"downloading_db2_data_backup_img_linux"},"menu":{"hasChildren":false},"tocID":"downloading_db2_data_backup_img_linux-d22908e24691","topics":[]},{"title":"Downloading a Db2 data backup image on Windows","shortdesc":"<p class=\"shortdesc\">Download the data backup image for a Db2 database on a Windows machine using the         fetch tool. You can use this image to perform data recovery for the required Db2         databases.<\/p>","href":"db2\/downloading_db2_data_backup_img_win.html","attributes":{"data-id":"downloading_db2_data_backup_img_win"},"menu":{"hasChildren":false},"tocID":"downloading_db2_data_backup_img_win-d22908e24702","topics":[]},{"title":"Downloading a Db2 log backup image on Linux","shortdesc":"<p class=\"shortdesc\">From a set of data backup images, download the log backup image for a database using         the standalone, Database Partitioning Feature, or High Availability Disaster Recovery         configuration. Then, use the downloaded backup image to retrieve transaction logs required         for verifying the Db2 data retrieval process on Rubrik CDM versions 9.0.1 or         later.<\/p>","href":"db2\/downloading_db2_log_backup_img_linux.html","attributes":{"data-id":"downloading_db2_log_backup_img_linux"},"menu":{"hasChildren":false},"tocID":"downloading_db2_log_backup_img_linux-d22908e24713","topics":[]},{"title":"Downloading a Db2 log backup image on Windows","shortdesc":"<p class=\"shortdesc\">Download the log backup image for a Db2 database on a Windows machine using the fetch         tool. You can use this image to perform data recovery for the required Db2         databases.<\/p>","href":"db2\/downloading_db2_log_backup_img_win.html","attributes":{"data-id":"downloading_db2_log_backup_img_win"},"menu":{"hasChildren":false},"tocID":"downloading_db2_log_backup_img_win-d22908e24724","topics":[]},{"title":"Downloading Db2 log backup images for CDM versions 9.0.0 and 9.0.1","shortdesc":"<p class=\"shortdesc\">Download the log backup images for a database to retrieve transaction logs that are         required for verifying the Db2 data retrieval process for Rubrik CDM versions 9.0.0 and         9.0.1.<\/p>","href":"db2\/downloading_db2_log_backup_images_9.0.0_9.0.1.html","attributes":{"data-id":"downloading_db2_log_backup_images_9.0.0_9.0.1"},"menu":{"hasChildren":false},"tocID":"downloading_db2_log_backup_images_9.0.0_9.0.1-d22908e24735","topics":[]},{"title":"Downloading Db2 log backup images for CDM version 8.1","shortdesc":"<p class=\"shortdesc\">Download the log backup images for a database to retrieve transaction logs that are         required for verifying the Db2 data retrieval process for Rubrik CDM version 8.1.<\/p>","href":"db2\/downloading_db2_log_backup_images_8.1.html","attributes":{"data-id":"downloading_db2_log_backup_images_8.1"},"menu":{"hasChildren":false},"tocID":"downloading_db2_log_backup_images_8.1-d22908e24746","topics":[]},{"title":"Viewing workload IDs","shortdesc":"<p class=\"shortdesc\">Simplify database restoration in the Database Partitioning Feature and standalone         configurations and relic setup by viewing and obtaining the workload ID of a Db2 database.         Use that ID on the Db2 host to facilitate the database restoration process.<\/p>","href":"db2\/viewing_workload_id.html","attributes":{"data-id":"viewing_workload_id"},"menu":{"hasChildren":false},"tocID":"viewing_workload_id-d22908e24757","topics":[]}]}};