oxy_webhelp_navlinks = {"discovery_ad_domains_dc-d22908e29461":{"topics":[{"title":"Adding an AD domain controller","shortdesc":"<p class=\"shortdesc\">Add an Active Directory domain controller as a Windows host to a Rubrik cluster to         start protecting the domain controller.<\/p>","href":"adds\/adding_ad_domain_controller_2.html","attributes":{"data-id":"adding_ad_domain_controller"},"menu":{"hasChildren":false},"tocID":"adding_ad_domain_controller-d22908e29475","topics":[]},{"title":"Deleting an AD domain controller","shortdesc":"<p class=\"shortdesc\">As an <span class=\"ph\">RSC<\/span> user with the required privileges, delete a Active         Directory domain controller from a Rubrik cluster if the domain controller fails and you         want to stop protecting it.<\/p>","href":"adds\/deleting_ad_domain_controller_2.html","attributes":{"data-id":"deleting_ad_domain_controller"},"menu":{"hasChildren":false},"tocID":"deleting_ad_domain_controller-d22908e29486","topics":[]},{"title":"Viewing AD domain controllers","shortdesc":"<p class=\"shortdesc\">As an <span class=\"ph\">RSC<\/span> user with the required privileges, you can view the         discovered domain controllers. You can also view the domain controllers associated with a         specific AD domain and manage protection by assigning SLA Domains.<\/p>","href":"adds\/viewing_ad_domain_controllers.html","attributes":{"data-id":"viewing_ad_domain_controllers"},"menu":{"hasChildren":false},"tocID":"viewing_ad_domain_controllers-d22908e29500","topics":[]}]}};