oxy_webhelp_navlinks = {"ec2_ebs_file_search_and_recovery-d22872e13109":{"topics":[{"title":"Enabling file indexing for a specific EC2 snapshot","shortdesc":"<p class=\"shortdesc\">Enable file indexing of a snapshot of an Amazon EC2 instance or EBS volume for         enabling file recovery.<\/p>","href":"saas\/aws_enabling_file_indexing.html","attributes":{"data-id":"aws_enabling_file_indexing"},"menu":{"hasChildren":false},"tocID":"aws_enabling_file_indexing-d22872e13122","topics":[]},{"title":"Managing file indexing for all EC2 snapshots","shortdesc":"<p class=\"shortdesc\">Manage file indexing for Amazon EC2 instances or EBS volumes by turning on or turning         off file indexing. Turning on file indexing facilitates quick file recovery by allowing you         to search across indexed snapshots and initiate recovery. When it is no longer needed, you         can turn off file indexing.<\/p>","href":"saas\/aws_managing_file_indexing.html","attributes":{"data-id":"aws_managing_file_indexing"},"menu":{"hasChildren":false},"tocID":"aws_managing_file_indexing-d22872e13133","topics":[]},{"title":"Indexing limitations","shortdesc":"<p class=\"shortdesc\">Information about the file systems supported for indexing in <span class=\"ph\">RSC<\/span>.<\/p>","href":"saas\/filesystems_indexing_limitations.html","attributes":{"data-id":"fs_ondemand_indexing_limitations"},"menu":{"hasChildren":false},"tocID":"fs_ondemand_indexing_limitations-d22872e13144","topics":[]}]}};