oxy_webhelp_navlinks = {"fs_filesets-d22908e20802":{"topics":[{"title":"Fileset fields, rules, and value types","shortdesc":"<p class=\"shortdesc\">The Rubrik cluster interprets a fileset based on the values provided in the Include,         Exclude, and Do Not Exclude fields. The Rubrik cluster applies a set of rules to the values         provided in these fields and permits several types of values to be added to the         fields.<\/p>","href":"common\/fileset_fields_rules_value_types.html","attributes":{"data-id":"fileset_fields_rules_value_types"},"menu":{"hasChildren":false},"tocID":"fileset_fields_rules_value_types-d22908e20815","topics":[]},{"title":"Host-specific fileset description rules","shortdesc":"<p class=\"shortdesc\">Rules for fileset descriptions differ for host types, ensuring accurate file path         configurations, preventing backup errors, and optimizing backup processes for different         systems.<\/p>","href":"common\/host_specific_fileset_desc_rules.html","attributes":{"data-id":"host_specific_fileset_desc_rules"},"menu":{"hasChildren":false},"tocID":"host_specific_fileset_desc_rules-d22908e20826","topics":[]},{"title":"Accepted values for fileset descriptions","shortdesc":"<p class=\"shortdesc\">Rubrik clusters accept a specific set of values in fileset descriptions.<\/p>","href":"common\/accepted_values_fileset_descs.html","attributes":{"data-id":"accepted_values_fileset_descs"},"menu":{"hasChildren":false},"tocID":"accepted_values_fileset_descs-d22908e20837","topics":[]},{"title":"Fileset description rules","shortdesc":"<p class=\"shortdesc\">Fileset descriptions must meet specific requirements.<\/p>","href":"common\/fileset_description_rules.html","attributes":{"data-id":"fileset_description_rules"},"menu":{"hasChildren":false},"tocID":"fileset_description_rules-d22908e20848","topics":[]},{"title":"Regular expression handling","shortdesc":"<p class=\"shortdesc\">Fileset rules convert to regular expressions that follow the Boost regular expression         syntax guidelines.<\/p>","href":"common\/fileset_regular_expression_handling.html","attributes":{"data-id":"fileset_regular_expression_handling"},"menu":{"hasChildren":false},"tocID":"fileset_regular_expression_handling-d22908e20859","topics":[]},{"title":"Fileset regular expression conversions","shortdesc":"<p class=\"shortdesc\">The Rubrik cluster converts regular expressions used during the specification of a         fileset rule.<\/p>","href":"common\/fileset_regular_expression_conversions.html","attributes":{"data-id":"fileset_regular_expression_conversions"},"menu":{"hasChildren":false},"tocID":"fileset_regular_expression_conversions-d22908e20870","next":"fileset_regular_expression_conversions-d22908e20870"},{"title":"Fileset details","shortdesc":"<p class=\"shortdesc\">For every fileset, <span class=\"ph\">RSC<\/span> displays details including the fileset rules         and the number of hosts being protected through the fileset.<\/p>","href":"saas\/fs_fileset_details.html","attributes":{"data-id":"fs_fileset_details"},"menu":{"hasChildren":false},"tocID":"fs_fileset_details-d22908e20892","topics":[]},{"title":"Creating a fileset","shortdesc":"<p class=\"shortdesc\">Create a fileset to define a set of data in a file system. You can assign a fileset         to a host to protect the data set specified by the fileset on that host.<\/p>","href":"saas\/fs_creating_fileset.html","attributes":{"data-id":"fs_creating_fileset"},"menu":{"hasChildren":false},"tocID":"fs_creating_fileset-d22908e20906","topics":[]},{"title":"Deleting a fileset from a host","shortdesc":"<p class=\"shortdesc\">Delete the association between a fileset and a host to stop SLA Domain          protection of the selected host fileset.<\/p>","href":"saas\/fs_deleting_a_fileset_from_a_host.html","attributes":{"data-id":"fs_deleting_a_fileset_from_a_host"},"menu":{"hasChildren":false},"tocID":"fs_deleting_a_fileset_from_a_host-d22908e20917","topics":[]},{"title":"Deleting a fileset from the Rubrik cluster","shortdesc":"<p class=\"shortdesc\">Delete a fileset from the Rubrik cluster to remove all existing associations of that         fileset with the hosts that use the fileset to protect files and folders on the         hosts.<\/p>","href":"saas\/fs_deleting_a_fileset_from_the_cluster.html","attributes":{"data-id":"fs_deleting_a_fileset_from_the_cluster"},"menu":{"hasChildren":false},"tocID":"fs_deleting_a_fileset_from_the_cluster-d22908e20928","topics":[]},{"title":"Fileset management actions","shortdesc":"<p class=\"shortdesc\">For every fileset, <span class=\"ph\">RSC<\/span> provides the ability to edit, clone, and delete the         fileset.<\/p>","href":"saas\/fs_fileset_management_actions.html","attributes":{"data-id":"fs_fileset_management_actions"},"menu":{"hasChildren":false},"tocID":"fs_fileset_management_actions-d22908e20940","topics":[]}]}};