oxy_webhelp_navlinks = {"high_availability_disaster_recovery-d22872e24916":{"topics":[{"title":"HADR setup management","shortdesc":"<p class=\"shortdesc\">With HADR setup management, you can gain control of your High Availability Disaster         Recovery environment, ensuring disaster recovery and minimizing downtime and data loss. This         includes the flexibility to delete HADR entities in any state, simplifying management tasks,         and enhancing data protection.<\/p>","href":"db2\/hadr_setup_management.html","attributes":{"data-id":"hadr_setup_management"},"menu":{"hasChildren":false},"tocID":"hadr_setup_management-d22872e24927","topics":[]},{"title":"Protecting an HADR entity","shortdesc":"<p class=\"shortdesc\">To ensure high availability and disaster recovery capabilities for your Db2 database,         protect an HADR entity for an SLA Domain.<\/p>","href":"db2\/protecting_hadr_entity.html","attributes":{"data-id":"protecting_hadr_entity"},"menu":{"hasChildren":false},"tocID":"protecting_hadr_entity-d22872e24938","topics":[]},{"title":"Refreshing HADR entities","shortdesc":"<p class=\"shortdesc\">When configuration changes or inconsistencies occur, refresh the HADR entity to         maintain consistent information between primary and standby instances of the Db2         database.<\/p>","href":"db2\/refreshing_hadr_entity.html","attributes":{"data-id":"refreshing_hadr_entity"},"menu":{"hasChildren":false},"tocID":"refreshing_hadr_entity-d22872e24949","topics":[]},{"title":"Deleting HADR entities","shortdesc":"<p class=\"shortdesc\">To remove an obsolete HADR configuration, delete the HADR entity associated with the         outdated configuration from the Db2 database.<\/p>","href":"db2\/deleting_hadr_entity.html","attributes":{"data-id":"deleting_hadr_entity"},"menu":{"hasChildren":false},"tocID":"deleting_hadr_entity-d22872e24960","topics":[]},{"title":"Removing the HADR protection","shortdesc":"<p class=\"shortdesc\">To adjust protection strategies or reallocate resources, remove the HADR protection         from SLA Domain assignments, excluding the HADR protection from upcoming backup and recovery         workflows.<\/p>","href":"db2\/removing_hadr_protection.html","attributes":{"data-id":"removing_hadr_protection"},"menu":{"hasChildren":false},"tocID":"removing_hadr_protection-d22872e24971","topics":[]}]}};