oxy_webhelp_navlinks = {"hyperv_batch_recovery-d22872e20270":{"topics":[{"title":"Recovering a batch of Hyper-V virtual machines using instant recovery in <span class=\"keyword\">Data Protection<\/span>","shortdesc":"<p class=\"shortdesc\">When you want to restore multiple Hyper-V virtual machines at one time, you can         perform an instant recovery to create new Hyper-V virtual machines from a selected snapshot         that replace the source virtual machines.<\/p>","href":"saas\/hyper_v\/hyperv_instant_batch_recovery.html","attributes":{"data-id":"hyperv_instant_batch_recovery"},"menu":{"hasChildren":false},"tocID":"hyperv_instant_batch_recovery-d22872e20281","topics":[]},{"title":"Recovering a batch of Hyper-V virtual machines using exports in <span class=\"keyword\">Data Protection<\/span>","shortdesc":"<p class=\"shortdesc\">When you want to recover multiple Hyper-V virtual machines at one time, you can         perform an export of multiple Hyper-V virtual machines to create new Hyper-V virtual         machines from a snapshot.<\/p>","href":"saas\/hyper_v\/hyperv_export_batch_recovery.html","attributes":{"data-id":"hyperv_export_batch_recovery"},"menu":{"hasChildren":false},"tocID":"hyperv_export_batch_recovery-d22872e20294","topics":[]},{"title":"Recovering a batch of Hyper-V virtual machines using live mounts in <span class=\"keyword\">Data Protection<\/span>","shortdesc":"<p class=\"shortdesc\">When you want to recover multiple Hyper-V virtual machines, you can create a Live         Mount for a batch of Hyper-V virtual machines, to create virtual machines from a         point-in-time copy of the source virtual machine.<\/p>","href":"saas\/hyper_v\/hyperv_mount_batch_recovery.html","attributes":{"data-id":"hyperv_mount_batch_recovery"},"menu":{"hasChildren":false},"tocID":"hyperv_mount_batch_recovery-d22872e20307","topics":[]}]}};