oxy_webhelp_navlinks = {"least_privilege_configuration_K8S-d22872e30027":{"topics":[{"title":"Configuring permissions to protect all resources","shortdesc":"<p class=\"shortdesc\">Create the kubeconfig file, and then set up the ClusterRole and ClusterRoleBinding to     grant the kubeconfig user the necessary permissions to protect all namespaces and cluster-wide     resources.<\/p>","href":"kubernetes\/k8s_configuring_perms_kubeconfig.html","attributes":{"data-id":"k8s_configuring_perms_kubeconfig"},"menu":{"hasChildren":false},"tocID":"k8s_configuring_perms_kubeconfig-d22872e30038","topics":[]},{"title":"Configuring namespace protection","shortdesc":"<p class=\"shortdesc\">Create the kubeconfig file, the ClusterRole, and the ClusterRoleBinding for the     kubeconfig user to assign the permissions required to protect all namespaces.<\/p>","href":"kubernetes\/k8s_configuring_namespace_protection.html","attributes":{"data-id":"k8s_configuring_namespace_protection"},"menu":{"hasChildren":false},"tocID":"k8s_configuring_namespace_protection-d22872e30049","topics":[]},{"title":"Configuring permissions to protect specific resources","shortdesc":"<p class=\"shortdesc\">Create the kubeconfig file, the ClusterRole, and the ClusterRoleBinding for the     kubeconfig user to use to assign the required permissions to protect specific resources within a     namespace.<\/p>","href":"kubernetes\/k8s_configuring_perms_specific_resources.html","attributes":{"data-id":"k8s_configuring_perms_specific_resources"},"menu":{"hasChildren":false},"tocID":"k8s_configuring_perms_specific_resources-d22872e30060","topics":[]},{"title":"Post-onboarding permissions updates","shortdesc":"<p class=\"shortdesc\">Rubrik recommends maintaining the most current privileges in your Kubernetes cluster.         The method for updating privileges depends on which onboarding method you         used.<\/p>","href":"kubernetes\/post_onboarding_perms_updates.html","attributes":{"data-id":"post_onboarding_perms_updates"},"menu":{"hasChildren":false},"tocID":"post_onboarding_perms_updates-d22872e30071","topics":[]}]}};