oxy_webhelp_navlinks = {"legal_hold_snapshot_rtn-d22908e24592":{"topics":[{"title":"Placing a legal hold on the Db2 database snapshot","shortdesc":"<p class=\"shortdesc\">To prevent the automatic expiration and removal of the Db2 database snapshot by the         Rubrik cluster, place a legal hold on the snapshot for compliance and data         retention.<\/p>","href":"db2\/placing_legal_hold_db2_db_snapshot.html","attributes":{"data-id":"placing_legal_hold_db2_db_snapshot"},"menu":{"hasChildren":false},"tocID":"placing_legal_hold_db2_db_snapshot-d22908e24603","topics":[]},{"title":"Removing a legal hold from the Db2 database snapshot","shortdesc":"<p class=\"shortdesc\">To return to regular retention management and optimize storage, remove the legal hold         from the Db2 database snapshot, allowing it to follow the SLA Domain policies and be managed         or deleted automatically as needed.<\/p>","href":"db2\/removing_legal_hold_db2_db_snapshot.html","attributes":{"data-id":"removing_legal_hold_db2_snapshot"},"menu":{"hasChildren":false},"tocID":"removing_legal_hold_db2_snapshot-d22908e24614","topics":[]}]}};