oxy_webhelp_navlinks = {"microsoft_purview_integration-d22908e39819":{"topics":[{"title":"Connecting to Microsoft Purview","shortdesc":"<p class=\"shortdesc\">You can connect to Microsoft Purview to detect and assign Microsoft Information         Protection labels that do not match data sensitivity findings in <span class=\"ph\">RSC<\/span>. When         configured, Rubrik will have visibility into the labels applied to your documents, and you         can define your own policies to identify mislabeled and unlabeled sensitive         data.<\/p>","href":"saas\/connecting_to_microsoft_purview.html","attributes":{"data-id":"connecting_to_microsoft_purview"},"menu":{"hasChildren":false},"tocID":"connecting_to_microsoft_purview-d22908e39830","topics":[]},{"title":"Assigning MIP labels","shortdesc":"<p class=\"shortdesc\">Assign Microsoft Information Protection (MIP) labels to documents on Microsoft 365         environments connected to the Microsoft Purview platform.<\/p>","href":"saas\/assigning_mip_labels.html","attributes":{"data-id":"assigning_mip_labels"},"menu":{"hasChildren":false},"tocID":"assigning_mip_labels-d22908e39844","topics":[]}]}};