oxy_webhelp_navlinks = {"mysql_recovery-d22872e25938":{"topics":[{"title":"MySQL recovery options","shortdesc":"<p class=\"shortdesc\"><span class=\"ph\">RSC<\/span> provides flexible and versatile options for MySQL recovery. You         can recover either the entire MySQL instance or specific databases, ensuring rapid and         granular data restoration from secure, immutable backups.<\/p>","href":"mysql\/mysql_recovery_options.html","attributes":{"data-id":"mysql_recovery_options"},"menu":{"hasChildren":false},"tocID":"mysql_recovery_options-d22872e25952","topics":[]},{"title":"Creating a restore mount point for MySQL instance snapshots","shortdesc":"<p class=\"shortdesc\">Create a mount point to restore your MySQL instance snapshots by mounting a snapshot         of backup data to the mount point using NFS.<\/p>","href":"mysql\/mysql_creating_restore_mp_instance_snap.html","attributes":{"data-id":"mysql_creating_restore_mp_instance_snap"},"menu":{"hasChildren":false},"tocID":"mysql_creating_restore_mp_instance_snap-d22872e25965","topics":[]},{"title":"Restoring MySQL snapshots to the original instance","shortdesc":"<p class=\"shortdesc\">Restore MySQL snapshots to the original MySQL instance from which the backup was         taken.<\/p>","href":"mysql\/mysql_restoring_snap_original_instance.html","attributes":{"data-id":"mysql_restoring_snap_original_instance"},"menu":{"hasChildren":false},"tocID":"mysql_restoring_snap_original_instance-d22872e25976","topics":[]},{"title":"Restoring MySQL snapshots to a new instance","shortdesc":"<p class=\"shortdesc\">Restore the MySQL instance snapshots to a newly created MySQL instance.<\/p>","href":"mysql\/mysql_restoring_snap_new_instance.html","attributes":{"data-id":"mysql_restoring_snap_new_instance"},"menu":{"hasChildren":false},"tocID":"mysql_restoring_snap_new_instance-d22872e25987","topics":[]},{"title":"Creating a restore mount point for MySQL database snapshots","shortdesc":"<p class=\"shortdesc\">Create a mount point for restoring your MySQL database snapshots by mounting a         snapshot of backup data to the mount point using NFS.<\/p>","href":"mysql\/mysql_creating_restore_mp_db_snap.html","attributes":{"data-id":"mysql_creating_restore_mp_db_snap"},"menu":{"hasChildren":false},"tocID":"mysql_creating_restore_mp_db_snap-d22872e25998","topics":[]},{"title":"Restoring MySQL snapshots to the original database","shortdesc":"<p class=\"shortdesc\">Restore the MySQL snapshots to the original MySQL database from which the backup was         taken.<\/p>","href":"mysql\/mysql_restoring_snap_original_db.html","attributes":{"data-id":"mysql_restoring_snap_original_db"},"menu":{"hasChildren":false},"tocID":"mysql_restoring_snap_original_db-d22872e26009","topics":[]},{"title":"Restoring MySQL snapshots to a new database","shortdesc":"<p class=\"shortdesc\">Restore the MySQL snapshots to a new MySQL database.<\/p>","href":"mysql\/mysql_restoring_snap_new_db.html","attributes":{"data-id":"mysql_restoring_snap_new_db"},"menu":{"hasChildren":false},"tocID":"mysql_restoring_snap_new_db-d22872e26020","topics":[]},{"title":"Unmounting the restore mount point after MySQL snapshot recovery","shortdesc":"<p class=\"shortdesc\">After recovering MySQL snapshots, unmount the restore mount point to remove the         metadata associated with the selected MySQL cluster.<\/p>","href":"mysql\/mysql_unmounting_restore_mp.html","attributes":{"data-id":"mysql_unmounting_restore_mp"},"menu":{"hasChildren":false},"tocID":"mysql_unmounting_restore_mp-d22872e26031","topics":[]}]}};