oxy_webhelp_navlinks = {"mysql_setup-d23139e25063":{"topics":[{"title":"Evaluation of authentication options for adding MySQL instance","shortdesc":"<p class=\"shortdesc\">When adding a MySQL instance to <span class=\"ph\">RSC<\/span>, it is important to evaluate the         available authentication options. Socket-based and TCP-based are two core authentication         methods for a MySQL client to verify its identity with the server.<\/p>","href":"mysql\/mysql_eval_auth_options.html","attributes":{"data-id":"mysql_eval_auth_options"},"menu":{"hasChildren":false},"tocID":"mysql_eval_auth_options-d23139e25077","topics":[]},{"title":"Prerequisites for MySQL setup","shortdesc":"<p class=\"shortdesc\">Information about prerequisites that you must complete before adding MySQL hosts and         instances to <span class=\"ph\">RSC<\/span>.<\/p>","href":"mysql\/mysql_setup_prerequisites.html","attributes":{"data-id":"mysql_setup_prerequisites"},"menu":{"hasChildren":false},"tocID":"mysql_setup_prerequisites-d23139e25091","topics":[]},{"title":"Configuration of MySQL instance","shortdesc":"<p class=\"shortdesc\">To configure MySQL instances with <span class=\"ph\">RSC<\/span>, integrate the MySQL         environment by registering Linux servers with a Rubrik cluster and installing the Rubrik         Backup Service agent. Using this setup, <span class=\"ph\">RSC<\/span> discovers MySQL instances,         requiring configuration details and credentials for effective backup and restore         management.<\/p>","href":"mysql\/mysql_instance_configuration.html","attributes":{"data-id":"mysql_instance_configuration"},"menu":{"hasChildren":false},"tocID":"mysql_instance_configuration-d23139e25105","next":"mysql_instance_configuration-d23139e25105"}]}};