oxy_webhelp_navlinks = {"nas_systems-d22872e34780":{"topics":[{"title":"NAS management","shortdesc":"<p class=\"shortdesc\">You can register, refresh, and delete NAS shares from the NAS systems page in <span class=\"ph\">Rubrik Security Cloud<\/span>.<\/p>","href":"nas\/nas_management.html","attributes":{"data-id":"nas_management"},"menu":{"hasChildren":false},"tocID":"nas_management-d22872e34793","next":"nas_management-d22872e34793"},{"title":"<span class=\"ph\">RSC<\/span> service account for NAS systems","shortdesc":"<p class=\"shortdesc\"><span class=\"ph\">RSC<\/span> creates a Rubrik service account and an associated role with the         necessary privileges on the NAS system and uses the service account for backup, restore, and         auto-discovery of NAS systems.<\/p>","href":"nas\/rsc_nas_service_account.html","attributes":{"data-id":"rsc_nas_service_account"},"menu":{"hasChildren":false},"tocID":"rsc_nas_service_account-d22872e34915","next":"rsc_nas_service_account-d22872e34915"},{"title":"Manage protection for NAS systems","shortdesc":"<p class=\"shortdesc\">You can manage protection for NAS shares and directories by assigning them an SLA         Domain.<\/p>","href":"nas\/nas_manage_protection.html","attributes":{"data-id":"nas_manage_protection"},"menu":{"hasChildren":false},"tocID":"nas_manage_protection-d22872e34958","next":"nas_manage_protection-d22872e34958"},{"title":"Manage protection for NetApp SnapMirror NAS volumes","shortdesc":"<p class=\"shortdesc\"><span class=\"ph\">RSC<\/span> backs up the latest snapshot of read-only NetApp SnapMirror         volumes, allowing the flexibility to specify a particular snapshot.<\/p>","href":"nas\/nas_manage_protection_netapp_snapmirror.html","attributes":{"data-id":"nas_manage_protection_netapp_snapmirror"},"menu":{"hasChildren":false},"tocID":"nas_manage_protection_netapp_snapmirror-d22872e35002","next":"nas_manage_protection_netapp_snapmirror-d22872e35002"},{"title":"SMB local user management of NAS components","shortdesc":"<p class=\"shortdesc\">SMB local user management of NAS components is crucial for mounting, protecting, and         managing SMB backup effectively, and for restore operations.<\/p>","href":"nas\/nas_smb_local_user_mgmt.html","attributes":{"data-id":"nas_smb_local_user_mgmt"},"menu":{"hasChildren":false},"tocID":"nas_smb_local_user_mgmt-d22872e35026","next":"nas_smb_local_user_mgmt-d22872e35026"},{"title":"On-demand snapshots of NAS shares and directories","shortdesc":"<p class=\"shortdesc\">In addition to policy-based snapshots, you can use the on-demand snapshot process to         take snapshots of protected NAS shares and directories.<\/p>","href":"nas\/nas_ods.html","attributes":{"data-id":"nas_ods"},"menu":{"hasChildren":false},"tocID":"nas_ods-d22872e35070","next":"nas_ods-d22872e35070"},{"title":"NAS shares recovery options","shortdesc":"<p class=\"shortdesc\"><span class=\"ph\">Rubrik Security Cloud<\/span> supports recovery from backups of NAS shares.<\/p>","href":"nas\/nas_recovery_of_nas_shares.html","attributes":{"data-id":"recovery_of_nas_shares"},"menu":{"hasChildren":false},"tocID":"recovery_of_nas_shares-d22872e35103","next":"recovery_of_nas_shares-d22872e35103"}]}};