oxy_webhelp_navlinks = {"oracle_databases_rsc-d23139e25447":{"topics":[{"title":"Rubrik Backup Service","shortdesc":"<p class=\"shortdesc\">Rubrik Backup Service is required in order to perform automated discovery of Oracle         hosts, Oracle RAC systems, and Oracle databases.<\/p>","href":"rbs\/oracle_rbs.html","attributes":{"data-id":"oracle_rbs"},"menu":{"hasChildren":false},"tocID":"oracle_rbs-d23139e25461","next":"oracle_rbs-d23139e25461"},{"title":"Oracle configuration","shortdesc":"<p class=\"shortdesc\">Initial configuration of the Rubrik cluster for Oracle requires at least one open or     mounted Oracle database and a user account with SYSDBA privileges.<\/p>","href":"oracle\/oracle_config.html","attributes":{"data-id":"config_prereqs"},"menu":{"hasChildren":false},"tocID":"config_prereqs-d23139e25483","next":"config_prereqs-d23139e25483"},{"title":"Role based access","shortdesc":"<p class=\"shortdesc\">Rubrik provides role-based access control, along with options for integrating with         existing directory services.<\/p>","href":"oracle\/role_based_access.html","attributes":{"data-id":"role_based_access"},"menu":{"hasChildren":false},"tocID":"role_based_access-d23139e25611","next":"role_based_access-d23139e25611"},{"title":"Oracle SEPS wallet","shortdesc":"<p class=\"shortdesc\">The Oracle Secure External Password Store wallet feature provides additional security         for connections by not exposing your username and password on the command line when you log         in. The feature stores the password credentials in the client-side Oracle         wallet.<\/p>","href":"oracle\/oracle_seps_wallet.html","attributes":{"data-id":"oracle_seps_wallet"},"menu":{"hasChildren":false},"tocID":"oracle_seps_wallet-d23139e25677","next":"oracle_seps_wallet-d23139e25677"},{"title":"Oracle database management","shortdesc":"<p class=\"shortdesc\">Manage discovered Oracle hosts and Oracle RAC systems from the Rubrik Oracle         Databases page. <\/p>","href":"oracle\/rbk_oracle_database_management.html","attributes":{"data-id":"rbk_oracle_database_management"},"menu":{"hasChildren":false},"tocID":"rbk_oracle_database_management-d23139e25710","next":"rbk_oracle_database_management-d23139e25710"},{"title":"Export Oracle database logs to a USB device","shortdesc":"<p class=\"shortdesc\">You can use Rubrik REST APIs to export logs from multiple Oracle databases to USB         devices.<\/p>","href":"oracle\/export_database_logs_usb.html","attributes":{"data-id":"export_database_logs_usb"},"menu":{"hasChildren":false},"tocID":"export_database_logs_usb-d23139e25889","next":"export_database_logs_usb-d23139e25889"},{"title":"Oracle Data Guard on Rubrik clusters","shortdesc":"<p class=\"shortdesc\">Rubrik clusters support Oracle Data Guard configurations to back up primary and         standby databases and to discover new Data Guard groups.<\/p>","href":"oracle\/oracle_data_guard_rubrik_clusters.html","attributes":{"data-id":"oracle_data_guard_rubrik_clusters"},"menu":{"hasChildren":false},"tocID":"oracle_data_guard_rubrik_clusters-d23139e25944","next":"oracle_data_guard_rubrik_clusters-d23139e25944"},{"title":"Backups and archived redo logs","shortdesc":"<p class=\"shortdesc\">The Rubrik cluster protects Oracle databases with backups and archived redo log     backups, running as separate jobs and at different frequencies in the Rubrik     cluster.<\/p>","href":"oracle\/backups_arch_redo_logs.html","attributes":{"data-id":"backups_arch_redo_logs"},"menu":{"hasChildren":false},"tocID":"backups_arch_redo_logs-d23139e26168","next":"backups_arch_redo_logs-d23139e26168"},{"title":"Archive log restore","shortdesc":"<p class=\"shortdesc\">Archive log restore reinstates archived logs from a specified time range or log         sequence period.<\/p>","href":"oracle\/archive_log_restoration.html","attributes":{"data-id":"archive_log_restoration"},"menu":{"hasChildren":false},"tocID":"archive_log_restoration-d23139e26245","next":"archive_log_restoration-d23139e26245"},{"title":"Oracle pluggable databases","shortdesc":"<p class=\"shortdesc\">You can restore, mount, and clone Oracle pluggable databases on the Rubrik         cluster.<\/p>","href":"oracle\/oracle_pluggable_databases.html","attributes":{"data-id":"oracle_pluggable_databases"},"menu":{"hasChildren":false},"tocID":"oracle_pluggable_databases-d23139e26278","next":"oracle_pluggable_databases-d23139e26278"},{"title":"Taking an on-demand snapshot","shortdesc":"<p class=\"shortdesc\">Manually initiate a database snapshot in addition to the policy-based snapshots of         the database. Assign an SLA Domain to manage that snapshot.<\/p>","href":"oracle\/rbk_taking_an_on_demand_snapshot.html","attributes":{"data-id":"rbk_taking_an_on_demand_snapshot"},"menu":{"hasChildren":false},"tocID":"rbk_taking_an_on_demand_snapshot-d23139e26336","next":"rbk_taking_an_on_demand_snapshot-d23139e26336"},{"title":"Point-in-time recovery","shortdesc":"<p class=\"shortdesc\">Point-in-time recovery restores database and log snapshots to the defined point in     time.<\/p>","href":"oracle\/point-in-time_recovery.html","attributes":{"data-id":"point-in-time_recovery"},"menu":{"hasChildren":false},"tocID":"point-in-time_recovery-d23139e26358","topics":[]},{"title":"Number of channels in recovery","shortdesc":"<p class=\"shortdesc\">The value for the number of channels in recovery can be configured for Live Mount,         Clone, Restore, Instant Recovery, and Validation operations.<\/p>","href":"oracle\/number_of_channels_in_recovery.html","attributes":{"data-id":"number_of_channels_in_recovery"},"menu":{"hasChildren":false},"tocID":"number_of_channels_in_recovery-d23139e26369","topics":[]},{"title":"Recovery from RAC to standalone Oracle hosts","shortdesc":"<p class=\"shortdesc\">For <span class=\"ph\">RSC<\/span> running Rubrik CDM version 9.2 or later, you can perform live         mounts or clones of Oracle RAC snapshots to an Oracle standalone host.<\/p>","href":"oracle\/recovery_rac_standalone_oracle_hosts.html","attributes":{"data-id":"recovery_rac_standalone_oracle_hosts"},"menu":{"hasChildren":false},"tocID":"recovery_rac_standalone_oracle_hosts-d23139e26380","topics":[]},{"title":"Oracle recovery using SCNs","shortdesc":"<p class=\"shortdesc\">You can recover Oracle snapshots and mount archive logs using the system change         number instead of the date and time when the snapshot occurs, providing a more accurate         point of recovery.<\/p>","href":"oracle\/oracle_recovery_using_scns.html","attributes":{"data-id":"oracle_recovery_using_scns"},"menu":{"hasChildren":false},"tocID":"oracle_recovery_using_scns-d23139e26394","next":"oracle_recovery_using_scns-d23139e26394"},{"title":"Advanced Cloning Operations and custom PFILES","shortdesc":"<p class=\"shortdesc\">You can use the Advanced Cloning Operations or a custom PFILE options when you         require the target recovery operation to use a different configuration than the source         database backup.<\/p>","href":"oracle\/aco_custom_pfiles.html","attributes":{"data-id":"aco_custom_pfiles"},"menu":{"hasChildren":false},"tocID":"aco_custom_pfiles-d23139e26449","next":"aco_custom_pfiles-d23139e26449"},{"title":"Oracle Live Mount","shortdesc":"<p class=\"shortdesc\">An Oracle Live Mount creates a database from a point-in-time copy of the source     database to speed up recovery time.<\/p>","href":"oracle\/oracle_live_mount.html","attributes":{"data-id":"oracle_live_mount"},"menu":{"hasChildren":false},"tocID":"oracle_live_mount-d23139e26482","next":"oracle_live_mount-d23139e26482"},{"title":"Instant Recovery for Oracle","shortdesc":"<p class=\"shortdesc\">Instant Recovery for Oracle replaces an existing database with a point-in-time copy,     requiring specific configurations and directory setup.<\/p>","href":"oracle\/oracle_instant_recovery.html","attributes":{"data-id":"oracle_instant_recovery"},"menu":{"hasChildren":false},"tocID":"oracle_instant_recovery-d23139e26641","next":"oracle_instant_recovery-d23139e26641"},{"title":"Database clones for Oracle","shortdesc":"<p class=\"shortdesc\">You can clone an Oracle database by selecting a recovery point to clone to a target     location.<\/p>","href":"oracle\/about_db_clones.html","attributes":{"data-id":"about_db_clones"},"menu":{"hasChildren":false},"tocID":"about_db_clones-d23139e26677","next":"about_db_clones-d23139e26677"},{"title":"Advanced Oracle DB clone and mount parameters","shortdesc":"<p class=\"shortdesc\">A parameters file allows you to specify advanced recovery options for live mount and         cloning tasks.<\/p>","href":"oracle\/oracle_adv_clone_mount_params.html","attributes":{"data-id":"oracle_adv_clone_mount_params"},"menu":{"hasChildren":false},"tocID":"oracle_adv_clone_mount_params-d23139e26754","next":"oracle_adv_clone_mount_params-d23139e26754"},{"title":"Same Host Recovery","shortdesc":"<p class=\"shortdesc\">Recover a database to the source Oracle host or Oracle RAC.<\/p>","href":"oracle\/samehost_recover.html","attributes":{"data-id":"about_samehost_recover"},"menu":{"hasChildren":false},"tocID":"about_samehost_recover-d23139e26798","next":"about_samehost_recover-d23139e26798"},{"title":"Tablespace recovery","shortdesc":"<p class=\"shortdesc\">Restore a tablespace in place to the same database.<\/p>","href":"oracle\/tablespace_recovery.html","attributes":{"data-id":"tablespace_recovery"},"menu":{"hasChildren":false},"tocID":"tablespace_recovery-d23139e26843","next":"tablespace_recovery-d23139e26843"}]}};