oxy_webhelp_navlinks = {"oracle_recovery_using_scns-d22872e26987":{"topics":[{"title":"Live Mounting an Oracle database using an Oracle SCN","shortdesc":"<p class=\"shortdesc\">Recover Oracle snapshots using the system change number instead of the date and time         the snapshot occurs, providing a more accurate point of recovery.<\/p>","href":"oracle\/lm_oracle_db_using_oracle_scn.html","attributes":{"data-id":"lm_oracle_db_using_oracle_scn"},"menu":{"hasChildren":false},"tocID":"lm_oracle_db_using_oracle_scn-d22872e26998","topics":[]},{"title":"Performing a Same Host recovery using an Oracle SCN","shortdesc":"<p class=\"shortdesc\">Use a snapshot to recover a database to the production host using an Oracle         SCN.<\/p>","href":"oracle\/performing_same_host_recovery_oracle_scn.html","attributes":{"data-id":"performing_same_host_recovery_oracle_scn"},"menu":{"hasChildren":false},"tocID":"performing_same_host_recovery_oracle_scn-d22872e27009","topics":[]},{"title":"Performing an Instant Recovery using an Oracle SCN","shortdesc":"<p class=\"shortdesc\">Use Instant Recovery to replace a database with a point-in-time copy using an Oracle         SCN.<\/p>","href":"oracle\/performing_ir_oracle_scn.html","attributes":{"data-id":"performing_ir_oracle_scn"},"menu":{"hasChildren":false},"tocID":"performing_ir_oracle_scn-d22872e27020","topics":[]},{"title":"Validating a database snapshot using an Oracle SCN","shortdesc":"<p class=\"shortdesc\">To ensure that an Oracle database can be recovered or restored, you can validate the         data in the Oracle database snapshot using an Oracle SCN to verify that there are no corrupt         files or missing blocks.<\/p>","href":"oracle\/validating_db_snapshot_using_oracle_scn.html","attributes":{"data-id":"validating_db_snapshot_using_oracle_scn"},"menu":{"hasChildren":false},"tocID":"validating_db_snapshot_using_oracle_scn-d22872e27031","topics":[]}]}};