oxy_webhelp_navlinks = {"qauth_initial_setup-d22908e9051":{"topics":[{"title":"Enabling quorum authorization","shortdesc":"<p class=\"shortdesc\">Enable quorum authorization for your <span class=\"ph\">RSC<\/span> domain to enforce the         requirement of receiving approvals from a minimum number of users before allowing critical,         data-modifying operations to proceed.<\/p>","href":"qauth\/enabling_qauth.html","attributes":{"data-id":"enabling_qauth"},"menu":{"hasChildren":false},"tocID":"enabling_qauth-d22908e9062","topics":[]},{"title":"Updating quorum authorization settings","shortdesc":"<p class=\"shortdesc\">You can optimize the management of quorum authorization requests using the quorum         authorization settings. With the required privileges, update the quorum authorization         settings, such as the time-to-live setting for requests with manually scheduled actions and         the timeout setting to indicate the duration after which inactive requests         expire.<\/p>","href":"qauth\/updating_qauth_settings.html","attributes":{"data-id":"updating_qauth_settings"},"menu":{"hasChildren":false},"tocID":"updating_qauth_settings-d22908e9076","topics":[]},{"title":"Disabling quorum authorization","shortdesc":"<p class=\"shortdesc\">Disable quorum authorization for your <span class=\"ph\">RSC<\/span> domain to relax the         requirement of obtaining additional approvals before allowing critical, data-modifying         actions. Disabling quorum authorization cancels pending quorum authorization requests,         deletes the configured policies and the QAuth Admin role, and removes the permission to         approve or deny requests from the QAuth reviewer role.<\/p>","href":"qauth\/disabling_qauth.html","attributes":{"data-id":"disabling_qauth"},"menu":{"hasChildren":false},"tocID":"disabling_qauth-d22908e9087","topics":[]}]}};