oxy_webhelp_navlinks = {"replication_policy_changes-d23139e6834":{"topics":[{"title":"Replication policy disabled","shortdesc":"<p class=\"shortdesc\">When the replication policy is disabled, the Rubrik cluster does not create         additional replicas on the target Rubrik cluster.<\/p>","href":"replication\/replication_policy_disabled.html","attributes":{"data-id":"replication_policy_disabled"},"menu":{"hasChildren":false},"tocID":"replication_policy_disabled-d23139e6845","topics":[]},{"title":"Replication policy re-enabled","shortdesc":"<p class=\"shortdesc\">When a replication policy is disabled and then re-enabled, the Rubrik cluster does         not create replicas for existing snapshots and backups.<\/p>","href":"replication\/replication_policy_re_enabled.html","attributes":{"data-id":"replication_policy_re_enabled"},"menu":{"hasChildren":false},"tocID":"replication_policy_re_enabled-d23139e6856","topics":[]},{"title":"Replication retention period increased","shortdesc":"<p class=\"shortdesc\"> Increasing the replication period configured in the SLA Domain increases the         replication retention period for new snapshots.<\/p>","href":"replication\/replication_retention_period_increased.html","attributes":{"data-id":"replication_retention_period_increased"},"menu":{"hasChildren":false},"tocID":"replication_retention_period_increased-d23139e6867","topics":[]},{"title":"Replication retention period decreased","shortdesc":"<p class=\"shortdesc\">Decreasing the replication period configured in the SLA Domain decreases the         replication retention period for new snapshots.<\/p>","href":"replication\/replication_retention_period_decreased.html","attributes":{"data-id":"replication_retention_period_decreased"},"menu":{"hasChildren":false},"tocID":"replication_retention_period_decreased-d23139e6878","topics":[]}]}};