oxy_webhelp_navlinks = {"ri_threat_hunts-d23139e35674":{"topics":[{"title":"Turbo threat hunts and advanced threat hunts","shortdesc":"<p class=\"shortdesc\"><span class=\"ph\">RSC<\/span> provides two distinct types of threat hunts, turbo threat hunts         and advanced threat hunts to enhance security measures against potential         threats.<\/p>","href":"saas\/turbo_adv_threat_hunts.html","attributes":{"data-id":"turbo_adv_threat_hunts"},"menu":{"hasChildren":false},"tocID":"turbo_adv_threat_hunts-d23139e35685","topics":[]},{"title":"Viewing threat hunts","shortdesc":"<p class=\"shortdesc\">View the completed and in-progress threat hunts with details on matches found,         impacted objects, and impacted snapshots.<\/p>","href":"saas\/viewing_threat_hunts.html","attributes":{"data-id":"viewing_threat_hunts"},"menu":{"hasChildren":false},"tocID":"viewing_threat_hunts-d23139e35698","topics":[]},{"title":"Threat hunt details","shortdesc":"<p class=\"shortdesc\">The Threat Hunts page provides comprehensive information about threat hunts and the         matches identified over time.<\/p>","href":"saas\/threat_hunt_details.html","attributes":{"data-id":"threat_hunt_details"},"menu":{"hasChildren":false},"tocID":"threat_hunt_details-d23139e35709","topics":[]},{"title":"Threat hunt object details","shortdesc":"<p class=\"shortdesc\">Provides information about the threat hunt object details page. When you open a         threat hunt object, you can see all the details of the files that matched the         hunt.<\/p>","href":"saas\/threat_hunt_object_details.html","attributes":{"data-id":"threat_hunt_object_details"},"menu":{"hasChildren":false},"tocID":"threat_hunt_object_details-d23139e35720","topics":[]},{"title":"RBAC privileges for threat hunt","shortdesc":"<p class=\"shortdesc\">RBAC privileges are required to create a threat hunt and view threat hunt         results.<\/p>","href":"saas\/rbac_privileges_threat_hunt.html","attributes":{"data-id":"rbac_privileges_threat_hunt"},"menu":{"hasChildren":false},"tocID":"rbac_privileges_threat_hunt-d23139e35731","topics":[]},{"title":"Initiating a turbo threat hunt","shortdesc":"<p class=\"shortdesc\">A turbo-charged threat hunt scans for hashes of file types most often used by threat         agents. You can initiate this scan on both Rubrik clusters and cloud accounts.<\/p>","href":"saas\/initiating_turbo_threat_hunt.html","attributes":{"data-id":"initiating_turbo_threat_hunt"},"menu":{"hasChildren":false},"tocID":"initiating_turbo_threat_hunt-d23139e35742","topics":[]},{"title":"Initiating an advanced threat hunt","shortdesc":"<p class=\"shortdesc\">Initiate an advanced threat hunt to scan Rubrik backups for Indicators Of Compromise         and identify files at risk. Advanced threat hunt does not support cloud workloads. An         advanced threat hunt supports all three IOC types: YARA rule, files hash, and file         pattern.<\/p>","href":"saas\/initiating_advanced_threat_hunt.html","attributes":{"data-id":"initiating_advanced_threat_hunt"},"menu":{"hasChildren":false},"tocID":"initiating_advanced_threat_hunt-d23139e35753","topics":[]},{"title":"Best practices for configuring threat hunts","shortdesc":"<p class=\"shortdesc\">For optimal results, you can follow a set of best practices when configuring threat         hunts.<\/p>","href":"saas\/ri_best_practices_configuring_threat_hunts.html","attributes":{"data-id":"best_practices_for_configuring_threat_hunts"},"menu":{"hasChildren":false},"tocID":"best_practices_for_configuring_threat_hunts-d23139e35764","topics":[]}]}};