oxy_webhelp_navlinks = {"rsc_encryption_management-d23139e9867":{"topics":[{"title":"Data-at-rest encryption","shortdesc":"<p class=\"shortdesc\">Depending on the specific model of the Rubrik Brik, Rubrik clusters encrypt data at         rest using software or hardware encryption.<\/p>","href":"saas\/rsc_data_at_rest_encryption.html","attributes":{"data-id":"rsc_data_at_rest_encryption"},"menu":{"hasChildren":false},"tocID":"rsc_data_at_rest_encryption-d23139e9881","topics":[]},{"title":"Key management for data-at-rest encryption","shortdesc":"<p class=\"shortdesc\">With advanced key management, including key rotation, rekeying, and KMS integration,         Rubrik enhances data-at-rest encryption by providing secure and flexible management of         encryption keys to ensure ongoing protection of stored data.<\/p>","href":"saas\/key_mgmt_dare.html","attributes":{"data-id":"key_mgmt_dare"},"menu":{"hasChildren":false},"tocID":"key_mgmt_dare-d23139e9892","topics":[]},{"title":"Encryption management details","shortdesc":"<p class=\"shortdesc\">The Encryption Management page provides centralized access to encryption settings,         Key Management Systems, and encryption status, enabling effective management and monitoring         of encryption.<\/p>","href":"saas\/encryption_mgmt_details.html","attributes":{"data-id":"encryption_mgmt_details"},"menu":{"hasChildren":false},"tocID":"encryption_mgmt_details-d23139e9903","next":"encryption_mgmt_details-d23139e9903"},{"title":"Key Management Services","shortdesc":"<p class=\"shortdesc\">Rubrik supports Key Management Services integration so that you can use your own         encryption keys for data at rest. This Bring Your Own Key approach ensures Rubrik never         accesses your master keys.<\/p>","href":"saas\/key_management_service.html","attributes":{"data-id":"key_management_service"},"menu":{"hasChildren":false},"tocID":"key_management_service-d23139e9947","next":"key_management_service-d23139e9947"},{"title":"Performing a rekey","shortdesc":"<p class=\"shortdesc\">Perform a rekey of the master key and root key to update your encryption key to an         alternative encryption key. This ensures that your data location uses an up-to-date         encryption by transitioning to a new Key Management Service instance or specific         key.<\/p>","href":"saas\/performing_rekey.html","attributes":{"data-id":"performing_rekey"},"menu":{"hasChildren":false},"tocID":"performing_rekey-d23139e10030","topics":[]},{"title":"Updating the master key for a reader location","shortdesc":"<p class=\"shortdesc\">Update the master key to ensure reader locations can continue accessing archived data         after a rekey operation on the owner location.<\/p>","href":"saas\/updating_master_key.html","attributes":{"data-id":"updating_master_key"},"menu":{"hasChildren":false},"tocID":"updating_master_key-d23139e10041","topics":[]},{"title":"Rotating encryption keys","shortdesc":"<p class=\"shortdesc\">To provide enhanced encryption key security, you must rotate the encryption key or         switch to another root of trust for your encryption key.<\/p>","href":"saas\/rotating_encryption_keys.html","attributes":{"data-id":"rotating_encryption_keys"},"menu":{"hasChildren":false},"tocID":"rotating_encryption_keys-d23139e10052","topics":[]}]}};