oxy_webhelp_navlinks = {"rsc_rmfa_totp-d23139e9604":{"topics":[{"title":"Mandatory multi-factor authentication","shortdesc":"<p class=\"shortdesc\">Multi-factor authentication enforcement is mandatory for all user accounts in your             <span class=\"ph\">RSC<\/span> environment.<\/p>","href":"saas\/mandatory_mfa.html","attributes":{"data-id":"mandatory_mfa"},"menu":{"hasChildren":false},"tocID":"mandatory_mfa-d23139e9615","topics":[]},{"title":"Configuring MFA for your account","shortdesc":"<p class=\"shortdesc\">Increase the security of your account by enabling multifactor         authentication.<\/p>","href":"saas\/configuring_mfa_account.html","attributes":{"data-id":"configuring_mfa_account"},"menu":{"hasChildren":false},"tocID":"configuring_mfa_account-d23139e9629","topics":[]},{"title":"Managing Rubrik two-step verification enforcement","shortdesc":"<p class=\"shortdesc\">An administrator or <span class=\"ph\">RSC<\/span> account owner can enable or not         enable enforcement of multifactor authentication through Rubrik two-step         verification.<\/p>","href":"saas\/managing_rubrik_2step_verification.html","attributes":{"data-id":"managing_rubrik_2step_verification"},"menu":{"hasChildren":false},"tocID":"managing_rubrik_2step_verification-d23139e9640","topics":[]},{"title":"Enforcing Rubrik Two-step verification for specific users","shortdesc":"<p class=\"shortdesc\">A user with the required privileges can enforce multifactor authentication for         selected local user accounts.<\/p>","href":"saas\/enforcing_r2sv_for_specific_users.html","attributes":{"data-id":"enforcing_rubrik_two_step_verification_for_specific_users"},"menu":{"hasChildren":false},"tocID":"enforcing_rubrik_two_step_verification_for_specific_users-d23139e9654","topics":[]},{"title":"Changing the TOTP device","shortdesc":"<p class=\"shortdesc\">Rubrik Two-Step Verification uses time-based one-time passwords generated by a user         device as an additional authentication factor. A user can change which device is used to         generate this authentication factor from <span class=\"ph\">RSC<\/span>.<\/p>","href":"saas\/mfa_changing_dev.html","attributes":{"data-id":"mfa_changing_dev"},"menu":{"hasChildren":false},"tocID":"mfa_changing_dev-d23139e9665","topics":[]},{"title":"Resetting Rubrik Two-step verification","shortdesc":"<p class=\"shortdesc\">Reset the multifactor authentication configuration for a user to reconfigure Rubrik         Two-step verification from the beginning.<\/p>","href":"saas\/resetting_r2sv.html","attributes":{"data-id":"resetting_rubrik_two_step_verification"},"menu":{"hasChildren":false},"tocID":"resetting_rubrik_two_step_verification-d23139e9679","topics":[]},{"title":"Passkeys for MFA","shortdesc":"<p class=\"shortdesc\">Passkeys are available as an alternate method of MFA in <span class=\"ph\">RSC<\/span>. Passkeys         use biometrics or hardware security keys to verify user identity. After configuration,         passkeys become the primary method of MFA for users to log in to <span class=\"ph\">RSC<\/span>.<\/p>","href":"saas\/passkeys_mfa.html","attributes":{"data-id":"passkeys_mfa"},"menu":{"hasChildren":false},"tocID":"passkeys_mfa-d23139e9690","next":"passkeys_mfa-d23139e9690"}]}};