oxy_webhelp_navlinks = {"rubrik_backup_service-d23139e20503":{"topics":[{"title":"Obtaining Rubrik Backup Service connector","shortdesc":"<p class=\"shortdesc\">Download the Rubrik Backup Service connector to enable the connected Rubrik clusters         to restore folders and files over corrupted data in the file system on the host.<\/p>","href":"rbs\/rbs_obtaining_connector.html","attributes":{"data-id":"obtaining_rubrik_backup_service_connector"},"menu":{"hasChildren":false},"tocID":"obtaining_rubrik_backup_service_connector-d23139e20514","topics":[]},{"title":"Registering a host running the Rubrik Backup Service","shortdesc":"<p class=\"shortdesc\">After installing RBS on a virtual machine, register the virtual machine with <span class=\"ph\">RSC<\/span>. Registering the virtual machine enables <span class=\"ph\">RSC<\/span> to         coordinate backup functionality with the Rubrik Backup Service.<\/p>","href":"saas\/registering_an_rbs_host_2.html","attributes":{"data-id":"registering_an_rbs_host"},"menu":{"hasChildren":false},"tocID":"registering_an_rbs_host-d23139e20525","topics":[]},{"title":"Deploying RBS on Windows guests","shortdesc":"<p class=\"shortdesc\">You can set up a Rubrik cluster to automatically install and register RBS on a         supported Windows guest at the next scheduled or on-demand backup.<\/p>","href":"saas\/vs_deploying_rbs_windows_guest.html","attributes":{"data-id":"vs_deploying_rbs_windows_guest"},"menu":{"hasChildren":false},"tocID":"vs_deploying_rbs_windows_guest-d23139e20542","topics":[]}]}};