oxy_webhelp_navlinks = {"rubrik_backup_service-d23139e41324":{"topics":[{"title":"Obtaining Rubrik Backup Service connector","shortdesc":"<p class=\"shortdesc\">Download the Rubrik Backup Service connector to enable the connected Rubrik clusters         to restore folders and files over corrupted data in the file system on the host.<\/p>","href":"rbs\/rbs_obtaining_connector_3.html","attributes":{"data-id":"obtaining_rubrik_backup_service_connector"},"menu":{"hasChildren":false},"tocID":"obtaining_rubrik_backup_service_connector-d23139e41335","topics":[]},{"title":"Downloading Rubrik Backup Service for cloud-native protection","shortdesc":"<p class=\"shortdesc\">Download the Rubrik Backup Service for cloud-native protection to coordinate backup         activity between the Exocompute instance and <span class=\"ph\">RSC<\/span>.<\/p>","href":"rbs\/rbs_downloading_for_cnp_2.html","attributes":{"data-id":"downloading_rbs_cnp"},"menu":{"hasChildren":false},"tocID":"downloading_rbs_cnp-d23139e41346","topics":[]},{"title":"Minimum RBS privileges for Windows","shortdesc":"<p class=\"shortdesc\">RBS requires certain privileges and permissions on Windows for both installation and         regular operations.<\/p>","href":"rbs\/rbs_min_privileges_windows.html","attributes":{"data-id":"rbs_min_privileges_windows"},"menu":{"hasChildren":false},"tocID":"rbs_min_privileges_windows-d23139e41360","topics":[]},{"title":"Rubrik Backup Service registration to multiple Rubrik clusters","shortdesc":"<p class=\"shortdesc\">A single Rubrik Backup Service host can connect to multiple Rubrik clusters. <\/p>","href":"saas\/multi_cluster_rbs.html","attributes":{"data-id":"multi_cluster_rbs"},"menu":{"hasChildren":false},"tocID":"multi_cluster_rbs-d23139e41371","topics":[]},{"title":"Host registration with RBS on multiple clusters","shortdesc":"<p class=\"shortdesc\">A Rubrik Backup Service host can be registered to multiple Rubrik clusters by adding         the host to both source and target clusters.<\/p>","href":"saas\/rbs_host_registration_multiple_clusters.html","attributes":{"data-id":"rbs_host_registration_multiple_clusters"},"menu":{"hasChildren":false},"tocID":"rbs_host_registration_multiple_clusters-d23139e41382","topics":[]},{"title":"Changing the primary Rubrik cluster of a host","shortdesc":"<p class=\"shortdesc\">When RBS installed on a host is registered with multiple Rubrik clusters, you can use             <span class=\"ph\">RSC<\/span> to change the primary Rubrik cluster of a host.<\/p>","href":"saas\/changing_primary_cluster_host.html","attributes":{"data-id":"changing_primary_cluster_host"},"menu":{"hasChildren":false},"tocID":"changing_primary_cluster_host-d23139e41393","topics":[]},{"title":"Installing RBS remotely on multiple hosts","shortdesc":"<p class=\"shortdesc\">From <span class=\"ph\">RSC<\/span>, you can install RBS remotely to manage and protect multiple         Linux, Solaris, HP-UX, AIX, or Windows hosts.<\/p>","href":"rbs\/rbs_remote_installing_multiple_hosts.html","attributes":{"data-id":"rbs_remote_installing_multiple_hosts"},"menu":{"hasChildren":false},"tocID":"rbs_remote_installing_multiple_hosts-d23139e41407","topics":[]},{"title":"RBS network throttling","shortdesc":"<p class=\"shortdesc\">RBS manages data backups and may consume all network bandwidth, affecting other         applications. Network throttling helps to limit the usage of RBS bandwidth and ensures that         essential services remain unaffected.<\/p>","href":"rbs\/rbs_network_throttling.html","attributes":{"data-id":"rbs_network_throttling"},"menu":{"hasChildren":false},"tocID":"rbs_network_throttling-d23139e41421","next":"rbs_network_throttling-d23139e41421"}]}};