oxy_webhelp_navlinks = {"salesforce_backup_recovery-d22908e30787":{"topics":[{"title":"Managing Salesforce protection","shortdesc":"<p class=\"shortdesc\">Protect data in a Salesforce organization by assigning an SLA Domain with defined         data protection policies.<\/p>","href":"salesforce\/managing_salesforce_data_protection.html","attributes":{"data-id":"managing_salesforce_data_protection"},"menu":{"hasChildren":false},"tocID":"managing_salesforce_data_protection-d22908e30800","topics":[]},{"title":"Taking an on-demand snapshot of a Salesforce instance","shortdesc":"<p class=\"shortdesc\">Take an on-demand snapshot to back up Salesforce data or metadata at any         time.<\/p>","href":"salesforce\/salesforce_taking_on_demand_snapshot.html","attributes":{"data-id":"salesforce_taking_on_demand_snapshot"},"menu":{"hasChildren":false},"tocID":"salesforce_taking_on_demand_snapshot-d22908e30811","topics":[]},{"title":"Recovering Salesforce records","shortdesc":"<p class=\"shortdesc\">Recover Salesforce records from a snapshot to a Salesforce production or sandbox         environment.<\/p>","href":"salesforce\/salesforce_recovering_records.html","attributes":{"data-id":"salesforce_recovering_records"},"menu":{"hasChildren":false},"tocID":"salesforce_recovering_records-d22908e30822","topics":[]},{"title":"Recovering Salesforce objects","shortdesc":"<p class=\"shortdesc\">Recover Salesforce objects from a snapshot to a Salesforce production or sandbox         environment. Recovering an object also recovers all its files and records.<\/p>","href":"salesforce\/salesforce_recovering_objects.html","attributes":{"data-id":"salesforce_recovering_objects"},"menu":{"hasChildren":false},"tocID":"salesforce_recovering_objects-d22908e30833","topics":[]},{"title":"Recovering Salesforce object metadata","shortdesc":"<p class=\"shortdesc\">Recover Salesforce custom object metadata associated with individual Salesforce         objects from a snapshot to a Salesforce production or sandbox environment. You can recover         all metadata related to individual objects, or you can recover specific metadata. Object         metadata that you can restore includes fields, validation rules, record types, and page         layouts.<\/p>","href":"salesforce\/salesforce_recovering_object_metadata.html","attributes":{"data-id":"salesforce_recovering_object_metadata"},"menu":{"hasChildren":false},"tocID":"salesforce_recovering_object_metadata-d22908e30844","topics":[]},{"title":"Recovering Salesforce general metadata","shortdesc":"<p class=\"shortdesc\">Recover Salesforce general metadata components, such as user permissions and reports,         from a snapshot to a Salesforce production or sandbox environment. General metadata includes         user permissions and reports. <\/p>","href":"salesforce\/salesforce_recovering_general_metadata.html","attributes":{"data-id":"salesforce_recovering_general_metadata"},"menu":{"hasChildren":false},"tocID":"salesforce_recovering_general_metadata-d22908e30855","next":"salesforce_recovering_general_metadata-d22908e30855"},{"title":"Salesforce sandbox seeding","shortdesc":"<p class=\"shortdesc\">You can send a subset of data from a snapshot created in a Salesforce production         environment to a sandbox environment. This allows you to use meaningful data for testing         your changes in an isolated environment before pushing the changes to         production.<\/p>","href":"salesforce\/salesforce_seeding.html","attributes":{"data-id":"salesforce_seeding"},"menu":{"hasChildren":false},"tocID":"salesforce_seeding-d22908e30888","next":"salesforce_seeding-d22908e30888"}]}};