oxy_webhelp_navlinks = {"sap_hana_storage_snapshots-d23139e27343":{"topics":[{"title":"Workflow for SAP HANA storage snapshots","shortdesc":"<p class=\"shortdesc\">Recovering an SAP HANA database using a storage snapshot requires you to meet         specific prerequisites and perform several tasks.<\/p>","href":"saas\/workflow_sap_hana_storage_snapshots.html","attributes":{"data-id":"workflow_sap_hana_storage_snapshots"},"menu":{"hasChildren":false},"tocID":"workflow_sap_hana_storage_snapshots-d23139e27357","topics":[]},{"title":"Recovering an SAP HANA storage snapshot","shortdesc":"<p class=\"shortdesc\">Recover an SAP HANA storage snapshot using <span class=\"ph\">RSC<\/span> to create storage and         make that storage available on the SAP HANA system primary host at a non-disruptive         temporary path. Then, an SAP HANA database administrator can mount the recovered storage to         the data volume path and recover the databases using SAP HANA cockpit and storage system         tools.<\/p>","href":"saas\/recovering_sap_hana_storage_snapshot.html","attributes":{"data-id":"recovering_sap_hana_storage_snapshot"},"menu":{"hasChildren":false},"tocID":"recovering_sap_hana_storage_snapshot-d23139e27368","topics":[]},{"title":"Assigning the SAP HANA permission for storage snapshot recovery","shortdesc":"<p class=\"shortdesc\">When you are performing the recovery operation for an SAP HANA storage snapshot and         the Azure subscription, containing the Azure virtual machines running the SAP HANA systems,         does not have the SAP HANA Snapshot - Recovery permission assigned, assign the permission to         ensure recovery.<\/p>","href":"saas\/assigning_sap_hana_perm_strg_snapsht_recovery.html","attributes":{"data-id":"assigning_sap_hana_perm_strg_snapsht_recovery"},"menu":{"hasChildren":false},"tocID":"assigning_sap_hana_perm_strg_snapsht_recovery-d23139e27382","topics":[]}]}};