oxy_webhelp_navlinks = {"snapshot_management-d23139e41116":{"topics":[{"title":"Retention management","shortdesc":"<p class=\"shortdesc\">You can assign retention policies to existing scheduled snapshots, on-demand       snapshots, and snapshots available in an archival location.<\/p>","href":"cdm\/retention_management_2.html","attributes":{"data-id":"retention_management"},"menu":{"hasChildren":false},"tocID":"retention_management-d23139e41131","next":"retention_management-d23139e41131"},{"title":"Legal hold for snapshot retention","shortdesc":"<p class=\"shortdesc\">Legal hold ensures that snapshots are retained indefinitely until the hold is         removed, overriding the scheduled expiry of an SLA Domain policy. This ensures that         snapshots are available as long as necessary to meet legal obligations.<\/p>","href":"saas\/legal_hold_snapshot_rtn_4.html","attributes":{"data-id":"legal_hold_snapshot_rtn"},"menu":{"hasChildren":false},"tocID":"legal_hold_snapshot_rtn-d23139e41266","next":"legal_hold_snapshot_rtn-d23139e41266"}]}};