oxy_webhelp_navlinks = {"snmp_integration-d23139e40872":{"topics":[{"title":"Rubrik MIB file","shortdesc":"<p class=\"shortdesc\">The MIB file on the Rubrik cluster contains the management data of the devices on the         IP network. You can download the MIB file from the Rubrik cluster to view the measurements         and notification messages (traps) specified in the file.<\/p>","href":"cdm\/rubrik_mib_file.html","attributes":{"data-id":"rubrik_mib_file"},"menu":{"hasChildren":false},"tocID":"rubrik_mib_file-d23139e40885","topics":[]},{"title":"Downloading the Rubrik MIB file","shortdesc":"<p class=\"shortdesc\">To work with the SNMP feature, download the Rubrik MIB file.<\/p>","href":"saas\/downloading_rubrik_mib_file.html","attributes":{"data-id":"downloading_rubrik_mib_file"},"menu":{"hasChildren":false},"tocID":"downloading_rubrik_mib_file-d23139e40896","topics":[]},{"title":"Syslog messages as SNMP objects","shortdesc":"<p class=\"shortdesc\">The Management Information Base module inside the Rubrik cluster represents syslog         messages as SNMP objects.<\/p>","href":"cdm\/syslog_messages_as_snmp_objects.html","attributes":{"data-id":"syslog_messages_as_snmp_objects"},"menu":{"hasChildren":false},"tocID":"syslog_messages_as_snmp_objects-d23139e40907","topics":[]},{"title":"SNMP polling","shortdesc":"<p class=\"shortdesc\">SNMP managers can poll the SNMP agent on the Rubrik cluster and request information         using the SNMPv2c or SNMPv3 protocol.<\/p>","href":"cdm\/snmp_polling.html","attributes":{"data-id":"snmp_polling"},"menu":{"hasChildren":false},"tocID":"snmp_polling-d23139e40918","topics":[]},{"title":"Configuring SNMPv2c and SNMP v3 support","shortdesc":"<p class=\"shortdesc\">To allow an SNMP manager to poll the Rubrik cluster, configure SNMPv2c and SNMP v3 on         the Rubrik cluster.<\/p>","href":"saas\/configuring_snmpv2c_support.html","attributes":{"data-id":"configuring_snmpv2c_support"},"menu":{"hasChildren":false},"tocID":"configuring_snmpv2c_support-d23139e40929","topics":[]},{"title":"Adding trap receivers","shortdesc":"<p class=\"shortdesc\"> Trap receivers are configured by specifying the IP address or FQDN along with the         receiver port. Traps from the Rubrik MIB file are send to a trap receiver for further         processing.<\/p>","href":"saas\/adding_trap_receivers_rsc.html","attributes":{"data-id":"adding_trap_receivers_rsc"},"menu":{"hasChildren":false},"tocID":"adding_trap_receivers_rsc-d23139e40940","topics":[]}]}};