oxy_webhelp_navlinks = {"sql_server_databases-d22908e28473":{"topics":[{"title":"Point-in-time recovery","shortdesc":"<p class=\"shortdesc\">For a database that uses the full recovery model or the bulk-logged recovery model,         the Rubrik cluster uses a combination of a snapshot of the database and the database         transaction log backups to recover a database.<\/p>","href":"common\/sql_point_in_time_recovery.html","attributes":{"data-id":"sql_point_in_time_recovery"},"menu":{"hasChildren":false},"tocID":"sql_point_in_time_recovery-d22908e28484","topics":[]},{"title":"Live Mount","shortdesc":"<p class=\"shortdesc\">A Live Mount creates a new database from a point-in-time copy of the source database.         The Rubrik cluster provides a Samba share of the new database directly from the Rubrik         cluster storage layer.<\/p>","href":"common\/sql_live_mount.html","attributes":{"data-id":"sql_live_mount"},"menu":{"hasChildren":false},"tocID":"sql_live_mount-d22908e28495","topics":[]},{"title":"Microsoft SQL Server requirements","shortdesc":"<p class=\"shortdesc\">A Rubrik cluster provides data management and protection for Microsoft SQL Server databases         when specific requirements are met.<\/p>","href":"common\/sql_requirements.html","attributes":{"data-id":"sql_requirements"},"menu":{"hasChildren":false},"tocID":"sql_requirements-d22908e28506","next":"sql_requirements-d22908e28506"},{"title":"Rubrik Backup Service for SQL Server databases","shortdesc":"<p class=\"shortdesc\">The Rubrik Backup Service backs up and restores SQL Server databases and provides         enhanced integration with SQL Server protected resources and host systems.<\/p>","href":"saas\/mssql_rubrik_backup_service.html","attributes":{"data-id":"mssql_rubrik_backup_service"},"menu":{"hasChildren":false},"tocID":"mssql_rubrik_backup_service-d22908e28528","next":"mssql_rubrik_backup_service-d22908e28528"},{"title":"Windows Server hosts","shortdesc":"<p class=\"shortdesc\">Manage and protect Microsoft SQL Server databases through the Rubrik Backup Service running on         a Windows Server host.<\/p>","href":"common\/sql_windows_server_hosts.html","attributes":{"data-id":"sql_windows_server_hosts"},"menu":{"hasChildren":false},"tocID":"sql_windows_server_hosts-d22908e28550","next":"sql_windows_server_hosts-d22908e28550"},{"title":"Microsoft SQL Server database details","shortdesc":"<p class=\"shortdesc\">The Microsoft SQL Server database details page provides detailed information about the         database, its protection status, Live Mounts, available snapshots, database events and         snapshots.<\/p>","href":"saas\/mssql_database_details.html","attributes":{"data-id":"mssql_database_details"},"menu":{"hasChildren":false},"tocID":"mssql_database_details-d22908e28619","next":"mssql_database_details-d22908e28619"},{"title":"Microsoft SQL Server database protection","shortdesc":"<p class=\"shortdesc\">After adding a Windows Server host to a Rubrik cluster, the Microsoft SQL Server databases on         that host can be managed through the web UI.<\/p>","href":"common\/sql_server_database_protection.html","attributes":{"data-id":"sql_server_database_protection"},"menu":{"hasChildren":false},"tocID":"sql_server_database_protection-d22908e28663","next":"sql_server_database_protection-d22908e28663"},{"title":"SQL Change Block Tracking","shortdesc":"<p class=\"shortdesc\">Change Block Tracking uses a filter driver to track SQL database file changes as they     happen.<\/p>","href":"common\/sql_change_block_tracking.html","attributes":{"data-id":"sql_change_block_tracking"},"menu":{"hasChildren":false},"tocID":"sql_change_block_tracking-d22908e28751","next":"sql_change_block_tracking-d22908e28751"},{"title":"Restore and recovery of Microsoft SQL Server databases","shortdesc":"<p class=\"shortdesc\">A Rubrik cluster can be used to manage backup, restore, and recovery of Microsoft SQL         Server databases.<\/p>","href":"saas\/mssql_restore_recovery_SQLServer_databases.html","attributes":{"data-id":"mssql_restore_recovery_SQLServer_databases"},"menu":{"hasChildren":false},"tocID":"mssql_restore_recovery_SQLServer_databases-d22908e28784","next":"mssql_restore_recovery_SQLServer_databases-d22908e28784"},{"title":"Microsoft SQL Server log shipping","shortdesc":"<p class=\"shortdesc\">Automatically send transaction log backups from a primary database running on a primary     server instance to one or more secondary databases running on separate secondary server     instances by using the Microsoft SQL Server log shipping feature.<\/p>","href":"common\/sql_server_log_shipping.html","attributes":{"data-id":"sql_server_log_shipping"},"menu":{"hasChildren":false},"tocID":"sql_server_log_shipping-d22908e28918","next":"sql_server_log_shipping-d22908e28918"},{"title":"Windows Server Failover Clustering","shortdesc":"<p class=\"shortdesc\">The Rubrik cluster provides protection for Windows Server Failover Clustering at the         failover cluster instance level.<\/p>","href":"saas\/mssql_windows_server_failover_clustering.html","attributes":{"data-id":"mssql_windows_server_failover_clustering"},"menu":{"hasChildren":false},"tocID":"mssql_windows_server_failover_clustering-d22908e28985","next":"mssql_windows_server_failover_clustering-d22908e28985"},{"title":"Always On Availability Groups","shortdesc":"<p class=\"shortdesc\">The Rubrik cluster supports data protection for availability databases in an Always On     availability group.<\/p>","href":"common\/sql_always_on_availability_groups.html","attributes":{"data-id":"sql_always_on_availability_groups"},"menu":{"hasChildren":false},"tocID":"sql_always_on_availability_groups-d22908e29073","next":"sql_always_on_availability_groups-d22908e29073"}]}};