oxy_webhelp_navlinks = {"sso_users-d22872e8511":{"topics":[{"title":"Accessing the SSO user details","shortdesc":"<p class=\"shortdesc\">Access the list of SSO users and their associated details. Additionally, you can         manage SSO users by performing various actions, such as authorizing SSO users, editing         direct role assignments, locking and hiding SSO user accounts, and configuring email         notifications.<\/p>","href":"saas\/accessing_sso_user_details.html","attributes":{"data-id":"accessing_sso_user_details"},"menu":{"hasChildren":false},"tocID":"accessing_sso_user_details-d22872e8522","topics":[]},{"title":"Authorizing an SSO user","shortdesc":"<p class=\"shortdesc\">Authorize an SSO user directly from <span class=\"ph\">RSC<\/span> and assign the required roles         so that they can perform their tasks effectively.<\/p>","href":"saas\/authorizing_sso_user.html","attributes":{"data-id":"authorizing_sso_user"},"menu":{"hasChildren":false},"tocID":"authorizing_sso_user-d22872e8533","topics":[]},{"title":"Editing the directly assigned roles for an authorized SSO user","shortdesc":"<p class=\"shortdesc\">For an authorized SSO user in <span class=\"ph\">RSC<\/span>, edit the role assignment to         directly assign additional roles. This ensures that the user has the necessary permissions         to perform tasks effectively.<\/p>","href":"saas\/editing_role_authorized_sso_user.html","attributes":{"data-id":"editing_role_authorized_sso_user"},"menu":{"hasChildren":false},"tocID":"editing_role_authorized_sso_user-d22872e8547","topics":[]}]}};