oxy_webhelp_navlinks = {"tocId-d22872e8027":{"topics":[{"title":"Users and Access","shortdesc":"<p class=\"shortdesc\"><span class=\"ph\">RSC<\/span> provides role-based access control, and several methods for authenticating a         user account.<\/p>","href":"saas\/users_access.html","attributes":{"data-id":"users_access"},"menu":{"hasChildren":true},"tocID":"users_access-d22872e8033","next":"users_access-d22872e8033"},{"title":"Quorum authorization","shortdesc":"<p class=\"shortdesc\">Quorum authorization is a security feature that enforces the requirement of getting         additional approvals before a requester can perform data-modifying actions in <span class=\"ph\">RSC<\/span>.<\/p>","href":"qauth\/quorum_authorization.html","attributes":{"data-id":"quorum_authorization"},"menu":{"hasChildren":true},"tocID":"quorum_authorization-d22872e8915","next":"quorum_authorization-d22872e8915"},{"title":"Single sign-on","shortdesc":"<p class=\"shortdesc\"><span class=\"ph\">Rubrik Security Cloud<\/span> supports SSO using the SAML 2.0 standard. With SSO, you can         log in to <span class=\"ph\">RSC<\/span> using credentials associated with an identity         provider.<\/p>","href":"saas\/single_sign_on.html","attributes":{"data-id":"single_sign_on"},"menu":{"hasChildren":false},"tocID":"single_sign_on-d22872e9460","topics":[]},{"title":"SAML 2.0 metadata files","shortdesc":"<p class=\"shortdesc\">SAML 2.0 uses information from metadata files to exchange information between an         identity provider and a service provider, such as <span class=\"ph\">RSC<\/span>, to establish a trust         relationship.<\/p>","href":"saas\/sso_saml_metadata_files.html","attributes":{"data-id":"sso_saml_metadata_files"},"menu":{"hasChildren":false},"tocID":"sso_saml_metadata_files-d22872e9476","topics":[]},{"title":"<span class=\"ph\">RSC<\/span> SSO workflow","shortdesc":"<p class=\"shortdesc\"><span class=\"ph\">RSC<\/span> supports configuring single sign-on with an identity provider for         secure authentication.<\/p>","href":"saas\/rsc_sso_workflow.html","attributes":{"data-id":"rsc_sso_workflow"},"menu":{"hasChildren":true},"tocID":"rsc_sso_workflow-d22872e9490","next":"rsc_sso_workflow-d22872e9490"},{"title":"Okta integration workflow","shortdesc":"<p class=\"shortdesc\">The integration workflow provides a high-level view of the requirements for                 configuration of SSO with Okta.<\/p>","href":"saas\/okta_integration_workflow.html","attributes":{"data-id":"okta_integration_workflow"},"menu":{"hasChildren":true},"tocID":"okta_integration_workflow-d22872e9590","next":"okta_integration_workflow-d22872e9590"},{"title":"ADFS integration workflow","shortdesc":"<p class=\"shortdesc\">The workflow provides a high-level view of the tasks involved in configuring SSO with         ADFS.<\/p>","href":"saas\/adfs_integrate_workflow.html","attributes":{"data-id":"adfs_integrate_workflow"},"menu":{"hasChildren":true},"tocID":"adfs_integrate_workflow-d22872e9668","next":"adfs_integrate_workflow-d22872e9668"},{"title":"Azure SSO","shortdesc":"<p class=\"shortdesc\">Configuring single sign-on with Azure involves several individual tasks, which must be     performed in the correct order.<\/p>","href":"saas\/sso_azure.html","attributes":{"data-id":"sso_azure"},"menu":{"hasChildren":true},"tocID":"sso_azure-d22872e9833","next":"sso_azure-d22872e9833"},{"title":"OneLogin integration workflow","shortdesc":"<p class=\"shortdesc\">The OneLogin integration workflow provides a high-level view of the tasks involved in         configuring single sign-on with OneLogin.<\/p>","href":"saas\/onelogin_integration_workflow.html","attributes":{"data-id":"onelogin_integration_workflow"},"menu":{"hasChildren":true},"tocID":"onelogin_integration_workflow-d22872e9999","next":"onelogin_integration_workflow-d22872e9999"},{"title":"IP allowlist","shortdesc":"<p class=\"shortdesc\"><span class=\"ph\">RSC<\/span> administrators can specify IPv4 addresses for the IP allowlist.             <span class=\"ph\">RSC<\/span> blocks unauthorized login attempts and unauthenticated API calls         that originate from addresses that are not on the allowlist.<\/p>","href":"saas\/common\/ip_allowlist.html","attributes":{"data-id":"ip_allowlist"},"menu":{"hasChildren":true},"tocID":"ip_allowlist-d22872e10113","next":"ip_allowlist-d22872e10113"},{"title":"Authentication","shortdesc":"<p class=\"shortdesc\"><span class=\"ph\">Rubrik Security Cloud<\/span> authentication restricts access to a specified set of         users.<\/p>","href":"saas\/common\/saas_authentication.html","attributes":{"data-id":"saas_authentication"},"menu":{"hasChildren":true},"tocID":"saas_authentication-d22872e10186","next":"saas_authentication-d22872e10186"},{"title":"Certificate management","shortdesc":"<p class=\"shortdesc\"><span class=\"ph\">RSC<\/span> Certificate Management provides a centralized dashboard to manage         certificates across all connected Rubrik clusters and for <span class=\"ph\">RSC<\/span>         workflows.<\/p>","href":"saas\/rsc_certificate_management.html","attributes":{"data-id":"rsc_certificate_management"},"menu":{"hasChildren":true},"tocID":"rsc_certificate_management-d22872e10360","next":"rsc_certificate_management-d22872e10360"},{"title":"Encryption management","shortdesc":"<p class=\"shortdesc\">With the encryption management feature in <span class=\"ph\">RSC<\/span>, you can view         information about data-at-rest encryption for the Rubrik clusters and archives, access to         your key management services, and enhance data security through automated key rotation every         30 days.<\/p>","href":"saas\/rsc_encryption_management.html","attributes":{"data-id":"rsc_encryption_management"},"menu":{"hasChildren":true},"tocID":"rsc_encryption_management-d22872e10462","next":"rsc_encryption_management-d22872e10462"},{"title":"User account lockout","shortdesc":"<p class=\"shortdesc\">The user account lockout feature enhances system security by providing global         administrators the ability to lock and unlock user accounts. This feature also prevents         unauthorized access from brute force attacks and safeguards local user accounts by         automatically locking them after a configurable period of inactivity.<\/p>","href":"saas\/common\/user_account_lockout.html","attributes":{"data-id":"user_account_lockout"},"menu":{"hasChildren":true},"tocID":"user_account_lockout-d22872e10658","next":"user_account_lockout-d22872e10658"},{"title":"Session management","shortdesc":"<p class=\"shortdesc\">The session management feature enhances system security in the following ways: ending         inactive user sessions, ending active user sessions after a specific duration, and limiting         the number of concurrent sessions allowed for each user.<\/p>","href":"saas\/common\/session_management.html","attributes":{"data-id":"session_management"},"menu":{"hasChildren":true},"tocID":"session_management-d22872e10780","next":"session_management-d22872e10780"},{"title":"Password policy","shortdesc":"<p class=\"shortdesc\"><span class=\"ph\">RSC<\/span> administrators can enhance the security of their <span class=\"ph\">RSC<\/span> domain by using the password policy feature to configure password         complexity rules for local users.<\/p>","href":"saas\/password_policy.html","attributes":{"data-id":"password_policy"},"menu":{"hasChildren":true},"tocID":"password_policy-d22872e10847","next":"password_policy-d22872e10847"},{"title":"New device login notification","shortdesc":"<p class=\"shortdesc\"><span class=\"ph\">RSC<\/span> notifies you when a new device is used to log in to your <span class=\"ph\">RSC<\/span> account, providing details such as device name, IP address, and         location.<\/p>","href":"saas\/common\/sec_new_device_login_notification.html","attributes":{"data-id":"sec_new_device_login_notification"},"menu":{"hasChildren":true},"tocID":"sec_new_device_login_notification-d22872e10904","next":"sec_new_device_login_notification-d22872e10904"}]}};