oxy_webhelp_navlinks = {"user_account_lockout-d22872e10658":{"topics":[{"title":"Account locking","shortdesc":"<p class=\"shortdesc\">Locking a user account prevents login access, even with the correct credentials.         Accounts are automatically locked due to brute force attacks or user account inactivity,         based on <span class=\"ph\">RSC<\/span> configurations. Administrators can also manually lock accounts         to prevent unauthorized access.<\/p>","href":"saas\/common\/account_locking.html","attributes":{"data-id":"account_locking"},"menu":{"hasChildren":false},"tocID":"account_locking-d22872e10669","next":"account_locking-d22872e10669"},{"title":"Account unlocking","shortdesc":"<p class=\"shortdesc\">When the user account lockout feature has locked a user account to stop a brute force         attack or because of user inactivity, or if an administrator initiated the lockout, an         administrator can unlock it. After unlocking the account, the user can log in using the         correct credentials.<\/p>","href":"saas\/common\/account_unlocking.html","attributes":{"data-id":"account_unlocking"},"menu":{"hasChildren":false},"tocID":"account_unlocking-d22872e10719","next":"account_unlocking-d22872e10719"},{"title":"Configuring account lockout settings","shortdesc":"<p class=\"shortdesc\">As an <span class=\"ph\">RSC<\/span> user with global administrator privileges, you can set up         account lockout settings. This can include limits for failed login attempts, auto-unlock to         protect against brute force attacks, and locking out inactive users to secure accounts after         a period of inactivity.<\/p>","href":"saas\/common\/configuring_account_lockout_settings.html","attributes":{"data-id":"configuring_account_lockout_settings"},"menu":{"hasChildren":false},"tocID":"configuring_account_lockout_settings-d22872e10766","topics":[]}]}};